	[20] = {id = 20,quest_id = 20,begin_talk = "",end_talk = "",condition_talk = "",},
	[21] = {id = 21,quest_id = 21,begin_talk = "",end_talk = "[@talk]npc_1§我们又见面了，公子。;player§没想到竟能在这里遇见你。;npc_1§公子可要猜灯谜？;player§有何不可。;",condition_talk = "",},
	[22] = {id = 22,quest_id = 22,begin_talk = "",end_talk = "[@talk]npc_1§公子再请看这一题。;player§这……;",condition_talk = "",},
	[23] = {id = 23,quest_id = 23,begin_talk = "",end_talk = "[@talk]npc_2§（见你面露难色，上前一步）老先生果然学富五车，这题我想试一试。;npc_3§有道是巾帼不让须眉，小姐但试无妨！;npc_2§数千年往事，注到心头。把酒凌虚，叹滚滚英雄何在。想：汉习楼船；唐标铁柱；宋挥玉斧；元跨革囊。伟烈丰功，费尽移山心力。尽珠帘画栋，卷不及暮雨朝云。便断碣残碑，都付与苍烟落照。只赢得：几杵疏钟；半江渔火；两行秋雁；一枕清霜。;player§真是妙哉！;",condition_talk = "",},
	[24] = {id = 24,quest_id = 24,begin_talk = "",end_talk = "[@talk]npc_2§姑娘惊世之才，令老夫汗颜。;npc_3§前辈快快请起。;npc_2§这里有两件东西，是我家主人让我转交给二位的。;player§你们家主人认识我们？。;npc_2§你们二位即是有缘人，自然与我家先生相识。;",condition_talk = "",},
	[25] = {id = 25,quest_id = 25,begin_talk = "",end_talk = "[@talk]npc_1§采雪无亲无故，今日蒙殿下相救，这幅画便赠与公子，希望能对您有所帮助。;player§恩，你跟着延平姑姑去吧，她会好好安顿你的。;npc_1§公子保重。;",condition_talk = "",},
	[26] = {id = 26,quest_id = 26,begin_talk = "",end_talk = "[@talk]npc_1§我家先生平日喜欢吃鱼，附近的草鱼味道鲜美，若你能抓几条来，兴许他愿意为公子卜上一卦。;player§这有何难？;npc_1§只是附近的草鱼都被渔夫补走，若没点本事，恐怕不行。;",condition_talk = "",},
	[27] = {id = 27,quest_id = 27,begin_talk = "",end_talk = "[@talk]npc_2§(黄衫老者将一个“囚”字递给了你。)大胆，怎么会送我公子一个“囚”字这么不吉利。;npc_3§公子左辅右弼，显然是帝王之命！只是，公子之相实非在下所能判断！;player§老先生有什么话，尽管明言。;npc_2§哈哈，我家先生说，时机未到，公子聪颖自会体会。;npc_2§怎么又走了，真是个怪老头。;",condition_talk = "",},
	[28] = {id = 28,quest_id = 28,begin_talk = "",end_talk = "[@talk]npc_1§我们也该回宫去了，闭宫门的时辰就快到了。;player§我们走吧。;",condition_talk = "",},
	[29] = {id = 29,quest_id = 29,begin_talk = "",end_talk = "[@talk]npc_1§呦，前面不就是珍妃娘娘吗？这雨下这么大，她怎么就跪在雨中呢？;player§走，我们去看看。;",condition_talk = "",},
	[30] = {id = 30,quest_id = 30,begin_talk = "",end_talk = "",condition_talk = "",},
	[31] = {id = 31,quest_id = 31,begin_talk = "[@talk]player§我会帮你保密的，这些酒我来搬，顺便去看看珍妃娘娘吧。;<exit>",end_talk = "[@talk]npc_15§不知皇子殿下是否肯赏脸陪我一同喝上两杯？; player§娘娘有请，岂敢不从。;<exit>",condition_talk = "",},
	[32] = {id = 32,quest_id = 32,begin_talk = "",end_talk = "",condition_talk = "",},
