	[99] = {id = 99,quest_id = 99,begin_talk = "",end_talk = "[@talk]npc_61§皇兄！竟连你也……哼！父皇又昏过去了，母后忧心不已，让你前去见她一面……;<exit>",condition_talk = "",},
	[100] = {id = 100,quest_id = 100,begin_talk = "",end_talk = "[@talk]player§岐王殿下，家中先生略懂歧黄之术，听闻宣隆皇病重，想为殿下分忧。; npc_68§如此，便一同前去吧。;<exit>",condition_talk = "",},
	[101] = {id = 101,quest_id = 101,begin_talk = "",end_talk = "[@talk]npc_66§这药可有问题？; player§先生说，此药内混有西域毒药，恐怕……;<exit>",condition_talk = "",},
