	[104] = {id = 104,quest_id = 104,begin_talk = "",end_talk = "",condition_talk = "[@talk]npc_66§我起拟一封懿旨，由你带给沈驰。; player§侄儿定不负所托！; [@talk]npc_66§若你不嫌弃，便唤我一声母后吧！; player§是，母后。;<exit>",},
