	[114] = {id = 114,quest_id = 114,begin_talk = "",end_talk = "[@talk]npc_80§昨夜，这些马贼破门而入，真是吓坏我了！;<exit>",condition_talk = "[@talk]npc_78§前面有个村庄，不过浓烟滚滚的，这是发生了什么事？;<exit>",},
	[115] = {id = 115,quest_id = 115,begin_talk = "",end_talk = "",condition_talk = "[@talk]npc_77§公子，前面便是西门府上派来接应我们的人，这下不用担惊受怕了。;<exit>",},
	[116] = {id = 116,quest_id = 116,begin_talk = "",end_talk = "",condition_talk = "",},
	[117] = {id = 117,quest_id = 117,begin_talk = "[@talk]npc_84§属下伤势已痊愈多日，今日便来陪殿下练练刀法，切磋一二，殿下不必顾忌，尽管向我攻来！;<exit>",end_talk = "",condition_talk = "",},
	[118] = {id = 118,quest_id = 118,begin_talk = "",end_talk = "",condition_talk = "",},
	[119] = {id = 119,quest_id = 119,begin_talk = "",end_talk = "[@talk]player§事不宜迟，我们即刻前去拜访沈先生。;<exit>",condition_talk = "[@talk]npc_90§属下探寻多日，终于让我发现那沈驰的住处了，只不过来的时候遇上了点麻烦。;<exit>",},
	[120] = {id = 120,quest_id = 120,begin_talk = "",end_talk = "[@talk]npc_90§这沈驰的架子还真是大！我倒要看看他是何许人物。; player§无妨，如果他真有些本事，等再久也是值得的。;<exit>",condition_talk = "[@talk]npc_90§想不到这里竟机关重重。;<exit>",},
	[121] = {id = 121,quest_id = 121,begin_talk = "",end_talk = "[@talk]player§先生先别着急回绝，在下拜读先生的《律民论》，这可是施展抱负的最佳的机会。; npc_94§如此，我就随你走这一遭。;<exit>",condition_talk = "",},
	[122] = {id = 122,quest_id = 122,begin_talk = "",end_talk = "[@talk]npc_85§这个钱四海，不但不补偿工人们的薪金，还要辞退他们，也难怪他们要反抗了。;player§原来是这样，我倒是有个办法。;<exit>",condition_talk = "",},
