	[136] = {id = 136,quest_id = 136,begin_talk = "",end_talk = "",condition_talk = "",},
	[137] = {id = 137,quest_id = 137,begin_talk = "[@talk]npc_59§不必如此拘礼，你的姑母是我的嫂嫂，说起来你还应该称我一声姑姑呢。;player§侄儿胤空拜见姑姑。;npc_59§你起来吧，我早就听说康国要派来一位皇子，可是一直没有听到抵达秦都的消息，这件事想来太子应该知道的。;player§太子诸事繁忙，或许是忘记了吧。。;npc_59§太子身兼水军都督之职，百忙之中还不忘为父皇解忧， 但这疏于禀告之事不得不罚，此事便由平王负责。;",end_talk = "",condition_talk = "",},
