	[148] = {id = 148,quest_id = 148,begin_talk = "[@talk]npc_40§哼！今天我打不死你，也要烧死你！;player§（内心）燕琳分明是嫉妒我和瑶如之间的事情，这个刁蛮公主竟然是个不爱男人的怪物……;",end_talk = "",condition_talk = "",},
