	[152] = {id = 152,quest_id = 152,begin_talk = "",end_talk = "",condition_talk = "",},
	[153] = {id = 153,quest_id = 153,begin_talk = "[@talk]npc_54§殿下，请带我一起离开这里吧。;player§我想让你暂时留在岐王府。;npc_54§为何如此？;player§我那日在寿宴得罪了太子，如果太子着手对付我，恐怕我不会像原来那样自由，三日之内，假如我无法离开质子府，你可以请岐王把你送往那里，我借机可以向岐王面陈一切。;",end_talk = "",condition_talk = "",},
