	[164] = {id = 164,quest_id = 164,begin_talk = "",end_talk = "",condition_talk = "",},
	[165] = {id = 165,quest_id = 165,begin_talk = "[@talk]npc_40§如果你执意留下瑶如，我便一剑杀了你！;player§若要我把瑶如让给你不难，只需答应我一个条件！;npc_40§父皇现在病情严重，母后终日陪伴君侧，恐怕抽不出时间见你。;player§孙先生乃是大康名医，你可以举荐我们为皇上诊病，一来我可以见到皇后，二来孙先生或许可以医治皇上的沉疾。;npc_40§我若带你入宫面见皇后，你决不可反悔！;",end_talk = "",condition_talk = "",},
