	[175] = {id = 175,quest_id = 175,begin_talk = "",end_talk = "",condition_talk = "",},
	[176] = {id = 176,quest_id = 176,begin_talk = "[@talk_ls]npc_53§若是能远离这喧嚣尘世，超脱于世俗纷争之外，那该有多好……;player§恐怕母后对你的期许远远不止于此！;npc_53§母后始终无法理解我的心中所想，我最厌恶的就是权力纷争，勾心斗角，就算让我坐在龙椅上，我也无法成为一个好皇帝。;player§出生在帝王之家，很多事情根本由不得我们去选择。;npc_53§哎……今朝有酒今朝醉，我们兄弟今夜喝个一醉方休！;",end_talk = "",condition_talk = "",},
