	[204] = {id = 204,quest_id = 204,begin_talk = "",end_talk = "",condition_talk = "",},
	[205] = {id = 205,quest_id = 205,begin_talk = "",end_talk = "[@talk]npc_54§公子，侍卫都在外面。;player§我要是不把精力耗尽，晚上岂不是要随着这帮侍卫前去荒唐？;npc_54§还是让瑶如来伺候公子……;",condition_talk = "",},
	[206] = {id = 206,quest_id = 206,begin_talk = "",end_talk = "",condition_talk = "",},
	[207] = {id = 207,quest_id = 207,begin_talk = "",end_talk = "",condition_talk = "",},
	[208] = {id = 208,quest_id = 208,begin_talk = "",end_talk = "[@talk]npc_82§公子，要不要我们陪你？;player§在下有内人相陪，就不劳烦几位美人了！;npc_82§啐！有没有搞错，带着老婆居然还要到这里来！;player§……;",condition_talk = "",},
	[209] = {id = 209,quest_id = 209,begin_talk = "",end_talk = "",condition_talk = "",},
	[210] = {id = 210,quest_id = 210,begin_talk = "",end_talk = "[@talk]npc_54§公子你快听，外面声音很嘈杂的样子。;player§好像有些不对！赶快换好衣服。;",condition_talk = "",},
	[211] = {id = 211,quest_id = 211,begin_talk = "",end_talk = "",condition_talk = "",},
