	[214] = {id = 214,quest_id = 214,begin_talk = "",end_talk = "",condition_talk = "",},
	[215] = {id = 215,quest_id = 215,begin_talk = "[@talk]npc_54§公子小心！;",end_talk = "",condition_talk = "",},
	[216] = {id = 216,quest_id = 216,begin_talk = "",end_talk = "",condition_talk = "",},
	[217] = {id = 217,quest_id = 217,begin_talk = "[@talk]npc_78§殿下可不可以帮我拔出腿上的箭头……;player§我去捡些柴火，以作支撑，否则你的腿定会留下残疾。;npc_78§多谢殿下。;player§说谢谢的应该是我才对，刚才如果不是你出手杀了那两名马贼，恐怕我此刻已经倒在了他们的刀下。;",end_talk = "",condition_talk = "",},
	[218] = {id = 218,quest_id = 218,begin_talk = "[@talk_ls]npc_78§我以前经过回龙镇多次，可是从来没有听说过什么马贼，却不知这些人究竟是什么来路？;player§我们必须弄到一辆马车，才能尽快离开这里。你若不及时救治，怕是会有生命危险。;npc_78§距离这里最近的集市也要有一百多里的路途，恐怕我是走不动了……;player§我先去弄些吃得来，顺便看看外面的情况。;",end_talk = "",condition_talk = "",},
	[219] = {id = 219,quest_id = 219,begin_talk = "[@talk]npc_78§生死乃是上天注定，平王何须如此感叹，再说这点箭伤还不至于要属下的性命。;player§不如我去回龙镇去看看，也许能够找到马匹。;npc_78§平王不可只身犯险，不如将我留下，你们先行赶往前方集镇，找到车马，再差人接我如何？;player§我们一旦离开，你的安危便无法保障。不必说了，我还是去回龙镇看看。;npc_78§殿下……;",end_talk = "",condition_talk = "",},
	[220] = {id = 220,quest_id = 220,begin_talk = "",end_talk = "",condition_talk = "",},
	[221] = {id = 221,quest_id = 221,begin_talk = "[@talk]npc_80§我在得意居的地下有一间地窖，很多东西都藏在里面，这几天我一直都躲在里面，大火未能烧到，所以才躲过了这场浩劫。;player§原来如此。;npc_80§既然能够在劫后重逢，我们便可称得上有缘，我和公子做个交易如何？;player§什么交易？;npc_80§公子帮我运一些东西离开，我给公子提供一辆代步之车。;player§听起来的确公平。;",end_talk = "",condition_talk = "",},
	[222] = {id = 222,quest_id = 222,begin_talk = "",end_talk = "",condition_talk = "",},
	[223] = {id = 223,quest_id = 223,begin_talk = "",end_talk = "",condition_talk = "",},
	[224] = {id = 224,quest_id = 224,begin_talk = "[@talk]npc_86§钱老板于五日前来到了济州，他身上多处受伤，随他前来的两名侍卫伤情也是十分严重，我家主人专门为他请了名医诊治，现在他还在养伤。;player§钱老板没事就好。;",end_talk = "",condition_talk = "",},
	[225] = {id = 225,quest_id = 225,begin_talk = "",end_talk = "",condition_talk = "",},
	[226] = {id = 226,quest_id = 226,begin_talk = "[@talk]player§西门老板，不知这里可有一位名叫沈驰的官员？;npc_87§他好像是南门的城守……不过我已久未听闻他的消息了。;player§原来如此，左右无事，我到处逛逛。;npc_87§公子便当这里是自己家。;",end_talk = "",condition_talk = "",},
	[227] = {id = 227,quest_id = 227,begin_talk = "",end_talk = "",condition_talk = "",},
	[228] = {id = 228,quest_id = 228,begin_talk = "[@talk]npc_78§殿下，门口守卫说瑶如小姐傍晚出门到现在还未归。;player§这两天他心绪不宁，我该多关心她才是。;npc_78§都是小人失职，我原该跟着瑶如小姐的。;player§这与你无关，是我自己太过疏忽……;",end_talk = "",condition_talk = "",},
	[229] = {id = 229,quest_id = 229,begin_talk = "",end_talk = "",condition_talk = "",},
	[230] = {id = 230,quest_id = 230,begin_talk = "",end_talk = "",condition_talk = "",},
	[231] = {id = 231,quest_id = 231,begin_talk = "[@talk]Npc_90§七日前，有人花重金让我来济州杀田玉麟。;player§雇主是谁？;npc_唐90§平王恕罪，行有行规，唐昧不好透露。;player§这么说，田玉麟还没死？;npc_90§他从北疆逃跑，惹下大祸，但并没有死。;",end_talk = "",condition_talk = "",},
	[232] = {id = 232,quest_id = 232,begin_talk = "",end_talk = "",condition_talk = "",},
	[233] = {id = 233,quest_id = 233,begin_talk = "",end_talk = "",condition_talk = "",},
