	[237] = {id = 237,quest_id = 237,begin_talk = "",end_talk = "",condition_talk = "",},
	[238] = {id = 238,quest_id = 238,begin_talk = "[@talk]npc_94§听风，有客人来了吗？;player§在下龙胤空，在此等候先生多时。;npc_沈驰§龙公子请稍候，沈某沐浴更衣之后便来见你。;player§沈先生不必着急。;",end_talk = "",condition_talk = "",},
	[239] = {id = 239,quest_id = 239,begin_talk = "",end_talk = "",condition_talk = "",},
	[240] = {id = 240,quest_id = 240,begin_talk = "[@talk]npc_94§不知公子可有何凭据？;player§胤空来时遇到马贼，慌忙之中不慎将懿旨遗落。;npc_94§看来太后所托非人，你若能冲破我的荆棘丛我便与你谈一谈。;",end_talk = "",condition_talk = "",},
	[241] = {id = 241,quest_id = 241,begin_talk = "",end_talk = "",condition_talk = "",},
