	[248] = {id = 248,quest_id = 248,begin_talk = "",end_talk = "",condition_talk = "",},
	[249] = {id = 249,quest_id = 249,begin_talk = "",end_talk = "",condition_talk = "",},
	[250] = {id = 250,quest_id = 250,begin_talk = "",end_talk = "[@talk]npc_54§那本账簿是我父亲当年放款和经营的名单，里面涉及许多秦国的王族贵胄，哥哥此次回来就是为了找出这本账簿，以图对这些忘恩负义的小人进行报复。;player§无论如何，我不会让你牵扯进这件事，我们要尽快离开这个多事之地。;",condition_talk = "",},
	[251] = {id = 251,quest_id = 251,begin_talk = "",end_talk = "",condition_talk = "",},
	[252] = {id = 252,quest_id = 252,begin_talk = "[@talk]npc_94§再有两日便到秦都了……;player§沈先生醒了？;npc_94§我哪里能睡得着，这山间小路崎岖不平，沈某的这身骨头都快要散架了。;player§那在下去给先生弄些吃的吧。;",end_talk = "",condition_talk = "",},
	[253] = {id = 253,quest_id = 253,begin_talk = "",end_talk = "",condition_talk = "",},
	[254] = {id = 254,quest_id = 254,begin_talk = "",end_talk = "",condition_talk = "",},
	[255] = {id = 255,quest_id = 255,begin_talk = "",end_talk = "",condition_talk = "",},
	[256] = {id = 256,quest_id = 256,begin_talk = "[@talk]npc_103§大爷！上房早已经准备好了。;player§想不到这山野之中居然有如此美艳的女子。;npc_103§让大爷见笑了。;",end_talk = "",condition_talk = "",},
	[257] = {id = 257,quest_id = 257,begin_talk = "",end_talk = "",condition_talk = "",},
