	[263] = {id = 263,quest_id = 263,begin_talk = "",end_talk = "",condition_talk = "",},
	[264] = {id = 264,quest_id = 264,begin_talk = "[@talk]npc_97§你果然不是寻常人物，难怪有人会对你赞不绝口！;player§多谢姑娘夸奖。;npc_97§这叫断命七绝针，我已经将其中的一枚植入瑶如的体内。;player§你居然用如此恶毒的手段。;npc_97§我给你一个月的时间，如果找不出田氏账簿的下落，你就只有等着心上人痛苦地死在眼前。;",end_talk = "",condition_talk = "",},
