	[271] = {id = 271,quest_id = 271,begin_talk = "",end_talk = "",condition_talk = "",},
	[272] = {id = 272,quest_id = 272,begin_talk = "",end_talk = "",condition_talk = "",},
	[273] = {id = 273,quest_id = 273,begin_talk = "",end_talk = "[@talk]npc_1§以白晷的谋略定然可以看出此次北征，意在将他调离秦都，然而让九公主出嫁队伍随同大军出征，燕元宗就理所当然的落在后军之中，无形之中燕元宗的安全已经得到了保障。;player§正是如此。;npc_1§晶后之所以让平王和九公主同行，是摸透九公主地心思，只要平王在便不会自寻短见，只要顺利抵达淞江城，将公主交入高丽迎亲船队的手中，她的死活便已经无关紧要了。;player§我………怎么没有想到！;",condition_talk = "",},
	[274] = {id = 274,quest_id = 274,begin_talk = "",end_talk = "[@talk]npc_1§九公主之事，恐怕只有一个结局……;player§难道我要亲手将燕琳送入死路不成？;",condition_talk = "",},
	[275] = {id = 275,quest_id = 275,begin_talk = "",end_talk = "[@talk]npc_1§也不尽然，公子难道没听说过置之死地而后生的话吗？;player§孙先生有办法？;npc_1§如果七日醉使用得当，可以让人进入假死状态。;player§此计甚妙，如果九公主上船即死，高丽迎亲船定然不会接一具尸首返回，燕琳应该会逃脱此劫。;npc_1§既然如此，就请公子取些蛇胆来，老夫也好早日制药。;",condition_talk = "",},
	[276] = {id = 276,quest_id = 276,begin_talk = "",end_talk = "[@talk]npc_1§这次无论太后和白晷谁胜谁负，大秦必然动荡异常，公子必须早做打算。公子终有一日还需返回大康去的……;player§太后对付白晷倾尽全力，对大秦来说却是一次重创，大秦国运恐怕从此就会一蹶不振。;npc_1§也许这就是上天赐给公子地机会……;",condition_talk = "",},
	[277] = {id = 277,quest_id = 277,begin_talk = "",end_talk = "[@talk]npc_1§我虽然嫁入皇宫不久，可是已经看出的其中的微妙所在，爹爹这次远征，恐怕凶多吉少，答应我，照顾我爹爹！;player§我……尽力而为。;npc_1§你也要小心，太后为了对付我爹爹，会不惜牺牲任何的代价。;",condition_talk = "",},
	[278] = {id = 278,quest_id = 278,begin_talk = "",end_talk = "[@talk]npc_1§答应我，一定要活着回来……若不是你唤醒了我对生命的渴望，此刻我早已经离开了这个人世……你可以再吻我一次吗？;",condition_talk = "",},
	[279] = {id = 279,quest_id = 279,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，前方百姓阻住去路，我们暂时先在这里等候，等大军出城以后再出发如何？;player§我们去前面看看。;npc_1§队伍行进极为缓慢，许多士兵都在和亲人难舍难离，更有反战者乘乱闹事。;player§一将功成万骨枯，只要有战争便会死人，如果他们继续留在秦都享受暂时的安逸，恐怕迟早变成亡国奴。;",condition_talk = "",},
	[280] = {id = 280,quest_id = 280,begin_talk = "",end_talk = "[@talk]npc_1§各位大秦的父老乡亲，东胡已经从北方侵入了大秦，如果我们每个人都贪图安乐，明日可能他们就会攻破大秦的防线，我们此去不是为了皇上的疆土，而是为了在场的每一位！;player§大将军在边关抗击胡虏多年，英名远播，大秦百姓心目中地威信果真是极高。;",condition_talk = "",},
	[281] = {id = 281,quest_id = 281,begin_talk = "",end_talk = "[@talk]npc_1§属下想陪同公子一起前往北疆，就是不知道公子乐不乐意？;player§此行艰险，焦大哥还有众多孩儿要照顾的。;npc_1§属下本就是康国人，承蒙殿下抬爱，如今正是为殿下效力之时，若是退缩，孩子们长大怕是要笑话我了。;player§这一路有焦大哥相伴，胤空便可高枕无忧。;",condition_talk = "",},
	[282] = {id = 282,quest_id = 282,begin_talk = "",end_talk = "[@talk]npc_1§公子多喝两杯，睡上一觉，保管你神清气爽，疲惫全消。;player§我并不觉的劳累，只是有点心烦……;npc_1§公子可是为了九公主远嫁之事？;player§我有些事想对燕琳说，可是太后派来的这个晖宁公主终日对我虎视眈眈，我连说句话的机会都没有。;",condition_talk = "",},
	[283] = {id = 283,quest_id = 283,begin_talk = "",end_talk = "[@talk]npc_1§我还当什么事情，这事好办，待会我们潜入她账内将她击晕就是……;",condition_talk = "",},
	[284] = {id = 284,quest_id = 284,begin_talk = "",end_talk = "[@talk]npc_1§我从小生活在宫闱之中，从未享受过一刻幸福的生活，自从认识你以后，我才知道生命竟然如此美好而灿烂……;player§燕琳……;npc_1§好想闻闻自由的花香。;",condition_talk = "",},
	[285] = {id = 285,quest_id = 285,begin_talk = "",end_talk = "[@talk]npc_1§在你心中究竟有没有真正爱过我？;player§难道你以为，我会将自己的女人亲手送给别人？;npc_1§你以为我还会相信你？;player§我发誓，决不会让你嫁到高丽去……;",condition_talk = "",},
	[286] = {id = 286,quest_id = 286,begin_talk = "",end_talk = "[@talk]npc_1§你这混蛋，苦是再敢骗我，我让你永生永世碰不得女人。;player§我会用七日醉让你进入假死状态，然后九公主就会永远的从这个世界消失。;npc_1§嘘，有人！;player§大概是晖宁公主发现我不在，到你这找人来了。;",condition_talk = "",},
	[287] = {id = 287,quest_id = 287,begin_talk = "",end_talk = "[@talk]npc_1§九公主那边地营帐已经完全弄好，值班的士卒也已经安排好，今晚平王殿下可以安安稳稳的睡个好觉了。;player§看来今晚恐怕有大雪，我们不如去猎几只野兔，给大家解解馋。;",condition_talk = "",},
	[288] = {id = 288,quest_id = 288,begin_talk = "",end_talk = "[@talk]npc_1§但愿这雪千万不要下大了才好幸好此地距离淞江城已经不远，即使是下雪也不会耽误了我们的行程。;player§今晚可能会很冷，李将军还是让士卒将马匹的四蹄全部包起来，以免被雪冻伤。;",condition_talk = "",},
	[289] = {id = 289,quest_id = 289,begin_talk = "",end_talk = "[@talk]npc_1§马匹发出一声声惊恐的嘶鸣，我们恐怕遇上麻烦了！;player§李统领在前方开路，唐昧断后，受伤的兄弟在中间，其余的在两边掩护，大家一起冲出去。;",condition_talk = "",},
	[290] = {id = 290,quest_id = 290,begin_talk = "",end_talk = "[@talk]npc_1§救我！救我！;player§我去救她。;",condition_talk = "",},
	[291] = {id = 291,quest_id = 291,begin_talk = "",end_talk = "[@talk]npc_1§殿下快走，如今我们自身难保，先带公主离开这里！;player§我们……走。;npc_1§我们先抢占土丘的高处！;",condition_talk = "",},
	[292] = {id = 292,quest_id = 292,begin_talk = "",end_talk = "[@talk]npc_1§决不可放过一个活口！;player§就看你有没有这个本事了！;npc_1§杀！;",condition_talk = "",},
	[293] = {id = 293,quest_id = 293,begin_talk = "",end_talk = "[@talk]npc_1§他们好像开始撤退了！;player§这些究竟是什么人？;npc_1§公子你看，这些人的身上都有大鹏的纹身。;player§大秦军队中士兵有纹身之风，以大鹏和虎头最为常见，这帮袭击我们的敌人竟然是秦军。;",condition_talk = "",},
	[294] = {id = 294,quest_id = 294,begin_talk = "",end_talk = "[@talk]npc_1§公子，我们在那里暂时歇息一下，也好将兄弟们的伤势处理一下。;player§不可以停留太长时间，我担心敌人还会回来。;",condition_talk = "",},
	[295] = {id = 295,quest_id = 295,begin_talk = "",end_talk = "[@talk]npc_1§往北三十里会有一个集镇，我们在那里应该可以得到给养。;player§你带两名士兵在松林周围巡视一下，我们在这里歇息一个时辰之后出发。;",condition_talk = "",},
	[296] = {id = 296,quest_id = 296,begin_talk = "",end_talk = "[@talk]npc_1§我不要去高丽……我要回家……;player§琳儿！;npc_1§胤空！不要离开我……;",condition_talk = "",},
	[297] = {id = 297,quest_id = 297,begin_talk = "",end_talk = "[@talk]npc_1§我不要做什么公主，更不去什么高丽，你带我走好不好，只要能在你身边，日子过的再清苦我也愿意……;player§我保证一定会带你走，但不是现在……。;",condition_talk = "",},
	[298] = {id = 298,quest_id = 298,begin_talk = "",end_talk = "[@talk]npc_1§大家快快准备！敌人来了……;player§赶快把火堆熄灭。;",condition_talk = "",},
	[299] = {id = 299,quest_id = 299,begin_talk = "",end_talk = "[@talk]npc_1§哈哈哈，真是掩耳盗铃，以为藏在树上我就找不到了吗？给我放箭！;",condition_talk = "",},
	[300] = {id = 300,quest_id = 300,begin_talk = "",end_talk = "[@talk]npc_1§看你有几分能耐，今日就让我亲手结果了你。;",condition_talk = "",},
	[301] = {id = 301,quest_id = 301,begin_talk = "",end_talk = "[@talk]npc_1§殿下！;player§你们终于来了，看来上天还是眷顾着我们。;npc_1§属下来迟了。;player§来得早，不如来得巧，给我杀！;",condition_talk = "",},
	[302] = {id = 302,quest_id = 302,begin_talk = "",end_talk = "[@talk]npc_1§经过这场血战，我发现公子的刀法进展很快。;player§也许只有到了生死悬于一线的时候才能激发出我的潜质吧。;npc_1§公子已经初窥刀法的真谛，假以时日，前途不可限量。;player§哈哈哈。;",condition_talk = "",},
	[303] = {id = 303,quest_id = 303,begin_talk = "",end_talk = "[@talk]npc_1§我们在战斗中俘获了二十多名敌人，讯问之后却毫无发现，真正的幕后主使究竟是谁依然没有头绪。;player§或许，有一个人会知道。;",condition_talk = "",},
	[304] = {id = 304,quest_id = 304,begin_talk = "",end_talk = "[@talk]npc_1§你来做什么？;player§是我从秦都带来的伤药，对你的伤势也行能有些帮助。;npc_1§你给我……出去……;player§这么说，田玉麟还没死？;npc_1§如果我没有猜错，这次袭击的事情，公主应该知悉内情……;",condition_talk = "",},
	[305] = {id = 305,quest_id = 305,begin_talk = "",end_talk = "[@talk]player§公主一路之上故意延缓我们的的进程，意欲拉开我们和前方部队的距离，真正的用心胤空不要挑明了吧？;npc_1§你……;player§只是你万万没有想到，幕后的这个主使人竟然连你也不放过！;npc_1§你……信口雌黄……把他给我轰走……;",condition_talk = "",},
	[306] = {id = 306,quest_id = 306,begin_talk = "",end_talk = "[@talk]player§有封肃王的密函你想不想看？看不出你的这位皇兄竟然如此厉害，此事要是传到太后耳中，恐怕你的结局会很惨。;npc_1§你……以为……我会……怕她吗？若不是她……我皇兄……怎么会如……早死，若不是她……太子缘何会无辜被贬？;player§看来幕后主使果真是肃王！;npc_1§你竟然……;",condition_talk = "",},
	[307] = {id = 307,quest_id = 307,begin_talk = "",end_talk = "[@talk]npc_1§燕兴启竟然如此阴险？可是他破坏大秦和高丽的联姻真正目的何在？;player§破坏高丽和大秦联姻真正获利的是东胡，难道他和东胡之间早有默契？;npc_1§这么看来燕兴启的野心决不仅仅是让白晷陷入困境。;player§没错，我几乎可以断定，他想要对付的还有晶后。;",condition_talk = "",},
	[308] = {id = 308,quest_id = 308,begin_talk = "",end_talk = "[@talk]npc_1§公子有没有想过，我们若是按原计划行事一样会破坏高丽与大秦之间的联盟？无形之中成为燕兴启的帮凶！;player§眼前的形势下，我们必须要先完成晶后的使命，不然的话，我等再也没有在秦都立足的机会。;npc_1§马上就到目的地了，我们只能走一步看一步了。;",condition_talk = "",},
	[309] = {id = 309,quest_id = 309,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，在下已经从淞江城内征集了二十名少女陪伴公主一行前往高丽，损毁的嫁妆我己令人尽快补齐，明日午时之前应该可以一切就绪。;player§先生安排的很周到。;npc_1§下官已经将港口修饰一新，只等高丽迎亲船队抵达。平王今夜但可放心，我让人在驿站迎宾厅安排好了酒宴，还请平王移尊前往。;",condition_talk = "",},
	[310] = {id = 310,quest_id = 310,begin_talk = "",end_talk = "[@talk]npc_1§你因何这么久都不过来看我？胤空！带我走……我好怕……;",condition_talk = "",},
	[311] = {id = 311,quest_id = 311,begin_talk = "",end_talk = "[@talk]npc_1§我快要撑不下去了，我不想看到再有人为我流血，为我死亡……;player§明天我就会让你离开这个地方，远离这一切的不幸和杀戮。;npc_1§我要你永永远远不可以忘记我们之间的一切。;player§我不会忘……;",condition_talk = "",},
	[312] = {id = 312,quest_id = 312,begin_talk = "",end_talk = "[@talk]npc_1§迎亲的人就快到了，公子准备好了吗？;player§从离开秦都，我一直都在为此刻准备着。;npc_1§属下相信公子一定会得偿所愿。;",condition_talk = "",},
	[313] = {id = 313,quest_id = 313,begin_talk = "",end_talk = "[@talk]npc_1§这些高丽女子全都身负武功，步法极为奥妙。;player§不到万不得已，尽量不要动手。;",condition_talk = "",},
	[314] = {id = 314,quest_id = 314,begin_talk = "",end_talk = "[@talk]npc_1§高丽使臣金舜臣拜见大秦九公主陛下，请公主沐浴验身。;player§大胆！我堂堂大秦公主岂容你如此侮辱？;",condition_talk = "",},
	[315] = {id = 315,quest_id = 315,begin_talk = "",end_talk = "[@talk]npc_1§高丽国土虽小，也有自己的规矩和尊严，迎娶公主之前验身，乃是高丽宫中的规矩，平王殿下还请见谅！;player§大秦也一样有大秦的规矩，你若敢做出任何有辱大秦威严的事，这场婚事不谈也罢。;",condition_talk = "",},
	[316] = {id = 316,quest_id = 316,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，此事若是闹大，太后方面恐怕不好交待……;player§怕什么？高丽侮辱大秦在先，便是太后知道也不会任由他们如此放肆！;",condition_talk = "",},
	[317] = {id = 317,quest_id = 317,begin_talk = "",end_talk = "[@talk]npc_1§高丽国医官全慧乔参见平王殿下。;player§你便是负责验身的医官吧？;npc_1§公主请勿见怪，高丽皇室之中向来都有这样的规矩，为公主验身不仅是对高丽皇室负责，还可证实公主清誉，堵住小人攸攸之口。;player§既然如此，好吧。;",condition_talk = "",},
	[318] = {id = 318,quest_id = 318,begin_talk = "",end_talk = "[@talk]npc_1§啊！;player§发生什么事？;",condition_talk = "",},
	[319] = {id = 319,quest_id = 319,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下请留步，内舱乃是避讳之地，恐怕您前去有所不便。;player§你难道听不到里面的叫声吗？若是公主有什么闪失，你自问能够担待的起吗？;npc_1§全太医乃是我们高丽国第一神医，决不会有什么差池……;player§给我走开！;",condition_talk = "",},
	[320] = {id = 320,quest_id = 320,begin_talk = "",end_talk = "[@talk]npc_1§九公主生死未卜，还望给全太医一个机会……;player§大胆贱人，居然敢谋害公主，给我拿下！;",condition_talk = "",},
	[321] = {id = 321,quest_id = 321,begin_talk = "",end_talk = "[@talk]npc_1§我为公主验身之时，她便突然倒地，何来加害之说？;player§你杀了公主，说！到底是谁派你来加害公主的？;npc_1§我跟你们走，无论怎么说公主都是死在高丽的舰船上，必须要有人来承担这个责任……;",condition_talk = "",},
	[322] = {id = 322,quest_id = 322,begin_talk = "",end_talk = "[@talk]npc_1§若是……全太医受到任何伤害，我会让你死无……葬身之地！。;player§只怕她的生死并不是你说了算的。;npc_1§我会找到证明慧乔清白的证据。;",condition_talk = "",},
	[323] = {id = 323,quest_id = 323,begin_talk = "",end_talk = "[@talk]npc_1§你要杀了我？那位公主是不是没有死？;player§很抱歉，如果有选择，我不会这么做。;",condition_talk = "",},
	[324] = {id = 324,quest_id = 324,begin_talk = "",end_talk = "[@talk]npc_1§这件事终于告一段落，唐昧负责把九公主送到安全的地方，这张字条他让我一定交到公子手中。;",condition_talk = "",},
	[325] = {id = 325,quest_id = 325,begin_talk = "",end_talk = "[@talk]npc_1§皇上派人前来，请公子尽快前往前线，我们也该启程了。;player§皇上若是知道九公主的死讯还不知会闹出什么乱子来。;npc_1§看来一场责罚势难避免……;player§躲是躲不掉了，我们走吧。;",condition_talk = "",},
	[326] = {id = 326,quest_id = 326,begin_talk = "",end_talk = "[@talk]npc_1§这一路上到处都是逃亡的百姓，不知何时，百姓才能得到真正的安宁。;player§所以八国之间的内耗才是这片土地动荡不安的真正原因，想根除这一切，必须将八国重新统一成一个整体。;npc_1§公子躲开些，这些人中已经有人感染了瘟疫。;player§把他们控制起来吧，否则真的发生疫情，后果不堪设想。;",condition_talk = "",},
	[327] = {id = 327,quest_id = 327,begin_talk = "",end_talk = "[@talk]player§胤空参见陛下，胤空有负陛下所托，让公主蒙难，请陛下治罪！;npc_1§琳儿已死，惩罚你又有何用？你先起来吧。;player§（内心）皇上这反映实在太不寻常了。;",condition_talk = "",},
	[328] = {id = 328,quest_id = 328,begin_talk = "",end_talk = "[@talk]npc_1§东胡逼近蛮州防线，大战一触即发。可那白晷却让人三番五次的请我前往蛮州，分明是想让我身入险境。;player§皇兄乃是大秦的皇帝，只要你不启程，白晷也拿你没有任何办法。;npc_1§你不知道，那白晷早已将我要亲临蛮州前线慰劳将士的消息散布了出去。若是我继续藏身在这里，在大秦的将士眼里岂不失去了威信？;",condition_talk = "",},
	[329] = {id = 329,quest_id = 329,begin_talk = "",end_talk = "[@talk]npc_1§临来之前，母后反复交待，要我一定要在军中竖立威信，千万不要在白晷的面前失去的面子，我该怎么办？;player§皇兄的意思是……;npc_1§我打算在战事发生以前去蛮州一趟，顺便鼓舞一下将士的士气，然后尽快返回。;player§陛下明鉴。前往蛮州虽说要冒一定的风险，可是能借此竖立在军中的威信，利远大于弊。;npc_1§好！明日你便和我一起前往蛮州。;",condition_talk = "",},
	[330] = {id = 330,quest_id = 330,begin_talk = "",end_talk = "[@talk]npc_1§燕元宗竟然对燕琳之死无动于衷，如果不是我亲眼所见，我真的不敢相信。;player§不对！我总觉着有什么不对，可又找不出究竟哪里出了差错……;",condition_talk = "",},
	[331] = {id = 331,quest_id = 331,begin_talk = "",end_talk = "[@talk]npc_1§考虑这么多干吗？当务之急想到一个法子尽早脱身。;player§燕元宗要去蛮州慰问大军，恐怕到了那里脱身会很难，白晷何等人物，他岂会轻易放他回还。;npc_1§那你还赞同燕元宗前去？;player§整个北疆都是白晷的势力范围，我就算是阻拦他前去又有何作用？;npc_1§白晷对公子素有偏见，我担心他此次会趁机对付公子。;player§看来我要抱紧燕元宗的大腿，牢牢守住这堵挡风之墙。;",condition_talk = "",},
	[332] = {id = 332,quest_id = 332,begin_talk = "",end_talk = "[@talk]npc_1§二位久等了，微臣忙于视察防线，还请多多恕罪。;player§白大将军为国操劳，皇上感激还来不及呢，又怎会怪罪……;npc_1§北疆苦寒之地比不上秦都，不周之处还望二位海涵。;",condition_talk = "",},
	[333] = {id = 333,quest_id = 333,begin_talk = "",end_talk = "[@talk]npc_2§陛下亲临前线，激励众将士气，鼓舞全军信心，实乃大秦之幸，来！我等敬陛下一杯！;npc_2§朕只是尽本分罢了，大将军过奖了。;",condition_talk = "",},
	[334] = {id = 334,quest_id = 334,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下！白将军让你晚间去他的营帐，他有话问你。;player§胤空正想猎几只大雁，与将军寒暄一场。;",condition_talk = "",},
	[335] = {id = 335,quest_id = 335,begin_talk = "",end_talk = "[@talk]npc_1§听说你在送公主前往淞江城的路上遇到多次伏击，可知道是什么人做的吗？;player§那些人全都是军犯。;npc_1§军犯？;player§他们对公主的行进路线极为清除，而且我怀疑……燕启月就是通风报讯的那个内奸。;npc_1§你怀疑谁在主使这一切？;player§在没有确切证据以前，胤空不好乱说。;",condition_talk = "",},
	[336] = {id = 336,quest_id = 336,begin_talk = "",end_talk = "[@talk]npc_1§白某到现在都不明白，你为何不早日离开秦国，甘愿卷入这场漩涡之中。;player§对胤空来说，秦国和大康并没有什么分别。;npc_1§安逸有时比战争更为可怕，我也是刚刚明白这个道理……;",condition_talk = "",},
	[337] = {id = 337,quest_id = 337,begin_talk = "",end_talk = "[@talk]npc_1§平王好早！;player§火盆灭了，营帐冷得像冰窖一样，再睡下去恐怕要给冻僵了。;npc_1§陛下和白大将军已经前往牧场，特地让卑职来接你！;",condition_talk = "",},
	[338] = {id = 338,quest_id = 338,begin_talk = "",end_talk = "[@talk]npc_2§东胡此次合三十万之众全力攻击北川，显然对北川志在必得，我军必须先行到达北川，以逸待劳。与东胡大军在那里展开一场全面大战。;player§白大将军所言极是。;npc_2§这次有陛下随军亲征，定然士气如虹，击溃东胡已成定局。;npc_2§朕……也要去？;npc_2§陛下来北疆就是为了御驾亲征，这种大战自然少不了您的参预。;",condition_talk = "",},
	[339] = {id = 339,quest_id = 339,begin_talk = "",end_talk = "[@talk]npc_1§公子回来后始终神不守舍，究竟在想什么？;player§你有没有觉得燕元宗的表他现太过怪异？我决不相信一个人可以在短时间内发生如此巨大的改变。;npc_1§公子怀疑燕元宗并不是原来那个？这世上哪会有如此相象的人？;player§我几乎可以肯定，这个燕元宗是个冒牌货。;",condition_talk = "",},
	[340] = {id = 340,quest_id = 340,begin_talk = "",end_talk = "[@talk]npc_1§如果真如公子所说，晶后用替身取代燕元宗，那么她对白晷就再无顾忌，说不定会趁此良机将白晷一举除去。;player§眼下只有让他继续伪装下去，白晷的主要目标放在他的身上，总好过转移到我身上好些。;",condition_talk = "",},
	[341] = {id = 341,quest_id = 341,begin_talk = "",end_talk = "[@talk]npc_1§报！北川城城守赵青涛拒绝先锋营入城！;player§什么？这混蛋天大的胆子，居然敢做出如此大逆不道之事，难道他不清楚皇上也在军中吗？;npc_1§我们怎么办，是退回蛮州，还是留在这里阻击东胡大军？;player§（暗自）好你个北川城城守赵青涛！看来晶后的真正目的就是让白晷在蛮州和北川之间疲于奔命，我必须告诉他燕元宗是冒牌货这件事。;",condition_talk = "",},
	[342] = {id = 342,quest_id = 342,begin_talk = "",end_talk = "[@talk]player§胤空有几句话想私下对白将军说。;npc_1§你是不是早就知悉了太后的计划？;player§如果我能够提前洞察一切，就不会陷入目前的困境来。有件事我要告知大将军，军营中的皇上并非真正的皇上！;npc_1§这世上难道真的会有如此相似之人？;player§开始我也不相信，可是经过我的一番试探，发现此人身上有诸多可疑之处。;",condition_talk = "",},
	[343] = {id = 343,quest_id = 343,begin_talk = "",end_talk = "[@talk]npc_1§燕元宗之事千万不可泄漏出去，否则必然会军心大乱，到时候局面将不可收拾。;player§胤空明白。;npc_1§太后果然厉害。为了除掉白某竟然不惜舍弃大秦的疆土和这三十万士卒的性命……;",condition_talk = "",},
	[344] = {id = 344,quest_id = 344,begin_talk = "",end_talk = "[@talk]npc_1§报！东胡大军中的两百余辆投石车开始发动，涂满火油的万钧巨石被高高抛起在空中，如流星般向城墙飞来。;player§如今只有打胜这场仗，我们才有生存的希望。;",condition_talk = "",},
	[345] = {id = 345,quest_id = 345,begin_talk = "",end_talk = "[@talk]npc_1§东胡人频繁攻城证明他们也担心战事继续拖下去，损失将不可想象。北疆最冷的时候即将到来，只要我们能再坚守几日，东胡大军必然撤退。;player§可是城内的粮食也已经不多了。;npc_1§我最担心的还是将士们的士气，燕元宗之事已经传遍全军，很多士卒的内心已经接近绝望。;",condition_talk = "",},
	[346] = {id = 346,quest_id = 346,begin_talk = "",end_talk = "[@talk]npc_1§如今看来我若是战死沙场，无论是对太后还是对我都是一个最好的结局，她估计会为我追封爵位，风光厚葬。;player§大将军……;npc_1§我一度让权力蒙住了双眼，战场的血腥却让我的本性慢慢复苏起来。如果你能躲过这场劫难，我不会再阻拦你和绮儿交往。;",condition_talk = "",},
	[347] = {id = 347,quest_id = 347,begin_talk = "",end_talk = "[@talk]npc_1§东胡大军已经攻破城门，殿下快跟我走！;player§保护大将军！;",condition_talk = "",},
	[348] = {id = 348,quest_id = 348,begin_talk = "",end_talk = "[@talk]player§大将军！小心！;npc_1§终于可以回家了……;player§我跟你们拼了，今天杀一个赚一个！;",condition_talk = "",},
	[349] = {id = 349,quest_id = 349,begin_talk = "",end_talk = "[@talk]npc_1§你想死？哈哈哈哈，我偏要留下你的性命！;",condition_talk = "",},
	[350] = {id = 350,quest_id = 350,begin_talk = "",end_talk = "[@talk]npc_1§从今日起你们便是我东胡的俘虏，之后会有太医给你们诊治，你们最好给我放老实一点，顺从者生，逆反者死！;",condition_talk = "",},
	[351] = {id = 351,quest_id = 351,begin_talk = "",end_talk = "[@talk]npc_1§你的伤口必须处理一下，不然肌肤会被冻伤，这条腿恐怕就要废了。;player§不可能……不可能……;npc_1§你别怕，很快就会康复的。;player§样貌，声音都一模一样，你是……你是……;",condition_talk = "",},
	[352] = {id = 352,quest_id = 352,begin_talk = "",end_talk = "[@talk]npc_1§我是完颜大夫，你并未伤及筋骨，应该很快就会痊愈。;player§（暗自）完颜大夫？这究竟是怎么一回事？;npc_1§你按时清理伤口，注意保暖，应该没有大碍。;player§……好的。;",condition_talk = "",},
	[353] = {id = 353,quest_id = 353,begin_talk = "",end_talk = "[@talk]npc_1§今夜将军有令，每一组可得到一只羊腿，至于谁有口福吃到，就要看你们地本事。player§大家不要上当，胡人是在戏弄我们……让我们之间互相搏斗，从中取乐。;",condition_talk = "",},
	[354] = {id = 354,quest_id = 354,begin_talk = "",end_talk = "[@talk]npc_1§不过是杀死个俘虏，有什么大不了的，你的性命我要了，以我跟秃颜将军的关系，这个面子他一定会给我。player§你是？;",condition_talk = "",},
	[355] = {id = 355,quest_id = 355,begin_talk = "",end_talk = "[@talk]npc_1§我不管你们来自何方，原来是何种身份、地位，今天起你们只有一个主人，那就是我！服从我，我会让你你所向往的一切，违背我，你们所面临的只有死亡。;",condition_talk = "",},
	[356] = {id = 356,quest_id = 356,begin_talk = "",end_talk = "[@talk]npc_1§解开你们的衣襟，露出你们的胸膛，我要亲手为我的财产留下乌氏的印记。;player§主人我花去了多少银子？;npc_1§三千两。;player§如果这烙铁印去我的脸上，您不但要失去一个忠心耿耿的奴隶，而且失去的金钱远远不止这个数目;npc_1§是吗？给我一个理由！;player§拥有自尊的奴隶，远比失去自尊的奴隶更有价值。;npc_1§我留你一丝尊严，你首先要自己与别人的不同！;",condition_talk = "",},
	[357] = {id = 357,quest_id = 357,begin_talk = "",end_talk = "[@talk]npc_1§这三个月之内，我会提供给你们所需要的一切，而你们要竭尽一切所能去训练，我要让你们成为黑沙城里最勇猛的斗士！;",condition_talk = "",},
	[358] = {id = 358,quest_id = 358,begin_talk = "",end_talk = "[@talk]npc_1§从今天起，你们俩个人一组，在我的安排下开始训练。;player§是！;",condition_talk = "",},
	[359] = {id = 359,quest_id = 359,begin_talk = "",end_talk = "[@talk]npc_1§二十六名奴隶，经过这一次的筛选仅仅剩下了十五名。恭喜你们成功的进入下一阶段地训练，同时也赢得了一个月的生命。;",condition_talk = "",},
	[360] = {id = 360,quest_id = 360,begin_talk = "",end_talk = "[@talk]npc_1§今天我们俩都存活了下来，希望不会有一天，我们成为彼此的敌人……;player§只可惜，这样的一天总会到来。;npc_1§快躲开！;",condition_talk = "",},
	[361] = {id = 361,quest_id = 361,begin_talk = "",end_talk = "[@talk]npc_1§他们和今日被你打败的奴隶，来自同一个地方。;player§谢谢，在这样的境遇之中，感谢你愿意救我。;",condition_talk = "",},
	[362] = {id = 362,quest_id = 362,begin_talk = "",end_talk = "[@talk]npc_1§你今日的表现十分出色，这是你应得的。;",condition_talk = "",},
	[363] = {id = 363,quest_id = 363,begin_talk = "",end_talk = "[@talk]npc_1§三个月之期即将结束，你们四人之中只能留下两个，在这场最后的对决之中，你的对手就是阿东。;player§这就意味着我们中的一个即将死去！人生往往都是这样，你越是不想让他发生的事情，偏偏就会发生在你的身上。;",condition_talk = "",},
	[364] = {id = 364,quest_id = 364,begin_talk = "",end_talk = "[@talk]npc_1§二十名武士手持劲弩围绕在训练场的护栏外，只要我们们有任何的异动，他们的弩箭就会毫不犹豫的夺去我们的生命。;player§我们没有选择，出手吧。;",condition_talk = "",},
	[365] = {id = 365,quest_id = 365,begin_talk = "",end_talk = "[@talk]npc_1§住手！你们两个都留下！;",condition_talk = "",},
	[366] = {id = 366,quest_id = 366,begin_talk = "",end_talk = "[@talk]npc_1§看来命运带我们不薄。;player§别动，你受伤了。;npc_1§这还是，你第一次向我示好。;player§因为从这里走出去，就意味着我们之间再也不存在生死的较量。;",condition_talk = "",},
	[367] = {id = 367,quest_id = 367,begin_talk = "",end_talk = "[@talk]npc_1§温泉！美女！;player§乌奇泰无疑是一个合格的老板，他知道什么时候最应该手下的斗士适度的放松。;npc_1§看来你已经见识过了。;player§哈哈哈哈。;",condition_talk = "",},
	[368] = {id = 368,quest_id = 368,begin_talk = "",end_talk = "[@talk]npc_1§放松，野兽……;player§嗯~;npc_1§很少有斗士拥有你这么文雅的外表。;",condition_talk = "",},
	[369] = {id = 369,quest_id = 369,begin_talk = "",end_talk = "[@talk]player§你叫什么名字？;npc_1§你和别人不同，从来没有人问过我的名字。;player§你会告诉我吗？;npc_1§不，因为你不会再回到这里，更不会记得我……;",condition_talk = "",},
	[370] = {id = 370,quest_id = 370,begin_talk = "",end_talk = "[@talk]npc_1§过来，我有话问你。;player§有什么，你便问吧。;npc_1§我是你的主人，你居然并不怕我？;player§你给我生命，我带给你金钱，我为什么要怕你？;npc_1§果然不一般，陪我喝一杯。;",condition_talk = "",},
	[371] = {id = 371,quest_id = 371,begin_talk = "",end_talk = "[@talk]player§好酒，我去弄些烤羊腿配这好酒！;npc_1§自从我第一眼看到你，就知道你和其他的奴隶完全不同，你的眼睛里充满了野心和欲望。;player§是吗？;npc_1§我平生阅人无数，决不会看走眼！你决不是普通的秦国战俘，我敢断定你的血统要比他们高贵的多，这就是我没有为你打上烙印的真正原因。;",condition_talk = "",},
	[372] = {id = 372,quest_id = 372,begin_talk = "",end_talk = "[@talk]npc_1§告诉我，你究竟想要什么？;player§自由！;npc_1§可是我现在只是你手下的奴隶，一名生命无法自由支配的斗士！;player§你究竟想告诉我什么？;npc_1§我过去也是一名奴隶，你所看到的乌奇泰，是靠自己的双手一点一点重攀爬到今天的位置，重新夺回了自己的自由。我可以，你一样可以做到！;",condition_talk = "",},
	[373] = {id = 373,quest_id = 373,begin_talk = "",end_talk = "[@talk]npc_1§明日我们就会前往‘黑沙城’，你将会面对整个胡国第一流决斗士的挑战。想获得自由，便为我夺取这次大赛的冠军，这是你唯一的机会……多多练习吧。;",condition_talk = "",},
	[374] = {id = 374,quest_id = 374,begin_talk = "",end_talk = "[@talk]npc_1§知道乌奇泰为什么会在关键时刻保住我的性命吗？;player§你知道？;npc_1§在我们之前比武的那两个人一个当场死亡，一个受了重伤，乌奇泰把他拖去喂了恶犬。;player§竟然……;npc_1§说起来我真的应该感谢他们……;",condition_talk = "",},
	[375] = {id = 375,quest_id = 375,begin_talk = "",end_talk = "[@talk]npc_1§你们四人一组在这里住下来，厨师会为你们定制合理的菜谱，晚上提供少量的马奶酒。;player§我可以和阿东住在一起吗？;npc_1§当然，只要你们不闹事。;",condition_talk = "",},
	[376] = {id = 376,quest_id = 376,begin_talk = "",end_talk = "[@talk]npc_1§我叫突藉，是本地人，你们有什么不明白的，都可以问我。;player§二十八名斗士为什么只有我们四个参加预赛呢？;npc_1§因为我们是第一次参加决斗，以往并没有任何的战绩，必须要经过两场淘汰赛，才能够晋级决赛圈。;",condition_talk = "",},
	[377] = {id = 377,quest_id = 377,begin_talk = "",end_talk = "[@talk]npc_1§每年参加决斗的人数虽多，可是真正有实力的只有四家，皇室完颜家族，相国耶律赤眉，富商赫连家族，还有就是我们的主人乌奇泰……;player§可是乌奇泰手下共有二十八人参加比赛，难道除了我们以外的二十四人全部晋级比赛不成？;npc_1§那二十四人中只有两人直接晋级决赛，剩下的二十二人。是参加集体决斗地斗士。;player§集体决斗？;npc_1§这是胡人最为传统的决斗场面，集体决斗按常规会在最终决战以前举行，多支队伍捉对厮杀，场面惨烈到了极点。;",condition_talk = "",},
	[378] = {id = 378,quest_id = 378,begin_talk = "",end_talk = "[@talk]npc_1§决斗的日子马上就到了，从今日起，你们需要加倍的练习。;",condition_talk = "",},
	[379] = {id = 379,quest_id = 379,begin_talk = "",end_talk = "[@talk]npc_1§冰豹！轮到你出场了！;player§冰豹？;npc_1§今后这就是你的名字，冰豹！;",condition_talk = "",},
	[380] = {id = 380,quest_id = 380,begin_talk = "",end_talk = "[@talk]npc_1§我调查过所有斗士的实力，他们远不是你们的对手。你要注意掩饰自己的实力，不要让人从开始就将注意力集中在你身上，否则你很难走得更远。;",condition_talk = "",},
	[381] = {id = 381,quest_id = 381,begin_talk = "",end_talk = "[@talk]npc_1§冰豹！冰豹！冰豹！真的太厉害了！;",condition_talk = "",},
	[382] = {id = 382,quest_id = 382,begin_talk = "",end_talk = "[@talk]npc_1§为什么不听我的，你过早的暴露了你的实力！;player§我会为你搏取你需要的一切，你要给我自由。;npc_1§你有资格跟我谈条件吗？;player§你知不知道，我可以像宰杀一只羔羊一样杀掉你？;npc_1§宰杀一只羔羊你损失的仅仅是一头羊。而我对你来说意味着巨额地财富。;",condition_talk = "",},
	[383] = {id = 383,quest_id = 383,begin_talk = "",end_talk = "[@talk]npc_1§你果然和别的奴隶不同，居然会利用一切的机会给我施压。;player§我在压力中谋取自由，你在压力中谋求利益。在这一点上并没有什么贵贱之分。;npc_1§夺到冠军，我给你自由！;",condition_talk = "",},
	[384] = {id = 384,quest_id = 384,begin_talk = "",end_talk = "[@talk]npc_1§你已经成为黑沙城的百姓所熟知的斗士——冰豹！希望你能够穿着这套铠甲一直杀入决赛。;",condition_talk = "",},
	[385] = {id = 385,quest_id = 385,begin_talk = "",end_talk = "[@talk]npc_1§决赛不同于你之前参加的淘汰赛，对手的强大远远超出你的想象。;player§杀入决赛就意味着我要连过五关。;npc_1§主人很看好你。;player§他会信守承诺给我自由吗？;npc_1§如果你能给主人带来丰厚的回报，我想他会给你自由。;",condition_talk = "",},
	[386] = {id = 386,quest_id = 386,begin_talk = "",end_talk = "[@talk]npc_1§冰豹叔叔！我可以摸摸你的头盔吗？;",condition_talk = "",},
	[387] = {id = 387,quest_id = 387,begin_talk = "",end_talk = "[@talk]npc_1§我是来看冰豹叔叔的，我好想看看你面具下的样子！;player§谢谢你来看我，这枚徽章送给你。;npc_1§冰豹叔叔，你一定能够战胜所有的对手！;",condition_talk = "",},
	[388] = {id = 388,quest_id = 388,begin_talk = "",end_talk = "[@talk]npc_1§上场前，干了这一杯，祝你明日能够重获自由。;player§干！;npc_1§不要喝！耶律丞相找过我……耶委赤眉为这次的决斗掷下重金，他不想有任何的闪失。;player§所以你们就想出在酒中下毒的主意？。;",condition_talk = "",},
	[389] = {id = 389,quest_id = 389,begin_talk = "",end_talk = "[@talk]npc_1§此事和主人无关，都是我的主意，再说那酒中原非毒药，只是让人四肢酸软的松骨散;player§松骨散和毒药又有什么分别，同样都是夺去我的性命，只不过是早晚的问题。;npc_1§耶律赤眉派大军监视着这里，如果我们赢了这场决斗，恐怕耶律赤眉不会善罢甘休。;",condition_talk = "",},
	[390] = {id = 390,quest_id = 390,begin_talk = "",end_talk = "[@talk]npc_1§你害怕吗？;player§局势发展到眼前的地步就算怕又有何用？;npc_1§好汉子！比我当年要勇敢的多。这是从秃颜手中买下你的证明，只要你取得明日的胜利，我就会给你自由。;",condition_talk = "",},
	[391] = {id = 391,quest_id = 391,begin_talk = "",end_talk = "[@talk]npc_1§你们为了坚持自己的准则将会付出惨重的代价！;player§只要走入决斗场，我就只剩下一个选择，那就是尽快的杀死对手，在最短的时间取得胜利，这一点没有任何人能够改变……;",condition_talk = "",},
	[392] = {id = 392,quest_id = 392,begin_talk = "",end_talk = "[@talk]npc_1§希望你的鲜血能够配得上我手中的利剑。;player§我会跨过你的尸首去迎接自由！;",condition_talk = "",},
	[393] = {id = 393,quest_id = 393,begin_talk = "",end_talk = "[@talk]npc_1§冰豹叔叔……冰豹叔叔……你快醒醒！;",condition_talk = "",},
	[394] = {id = 394,quest_id = 394,begin_talk = "",end_talk = "[@talk]npc_1§你的运气真好。剑锋并没有伤到心肺。修养一段时间就会恢复如常，应该不会留下什么后患。;player§谢谢小姐相救……;npc_1§你应该谢的是翼虎少爷！;",condition_talk = "",},
	[395] = {id = 395,quest_id = 395,begin_talk = "",end_talk = "[@talk]npc_1§翼虎！胡闹够了没有？一声不吭地就走了，难道你就没有想过姐姐会担心？;player§翼虎少爷只是想要救在下罢了，还请完颜将军不要责怪。;npc_1§我何尝不让他去救人了，只是要经过我的同意。;player§在下替少爷，向完颜将军陪个不是。;",condition_talk = "",},
	[396] = {id = 396,quest_id = 396,begin_talk = "",end_talk = "[@talk]npc_1§快来人啊！有刺客！了……;player§究竟发生了什么事？为什么会受这么重的伤！;",condition_talk = "",},
	[397] = {id = 397,quest_id = 397,begin_talk = "",end_talk = "[@talk]npc_1§冰豹……这……这……是你的契约……我答应给你自由……我……这一生……只有一件憾事……，今日……你已……代我……完成……我乌奇泰……死而无……;",condition_talk = "",},
	[398] = {id = 398,quest_id = 398,begin_talk = "",end_talk = "[@talk]npc_1§决斗之后，主人本来想结束这里的一切尽早离去，可是耶律丞相派人召他入府议事，耶律丞相提出，买下整座乌氏行馆和这里的所有奴隶，而且他点名要买下你。;player§主人是怎么回答的？;npc_1§主人自然不肯答应，耶律赤眉恼羞成怒。我和主人担心他会施以报复，本想回到行馆马上带领你你离开，可是没想到……耶律赤眉这混账，在途中便对主人下手。;player§我绝不放过他们！;",condition_talk = "",},
	[399] = {id = 399,quest_id = 399,begin_talk = "",end_talk = "[@talk]npc_1§我本不愿接受，可是主人终身未娶，身边并无子嗣，再说他交给我这副担子的目的，是让我带着这帮手下渡过这场危机，这才不得不接受下来。;player§那耶律赤眉并不缺财富，买下乌氏行馆只是为了泻去心头之恨，若是你将我送到丞相府上，也许这件事他会就此作罢。;npc_1§冰豹兄如今已经是自由之身，我焉能为了乌氏地利益，将您推入火海之中，我之所以来找您商量，是因为主人生前对您极为推崇，无论心智胆略都远在我等之上。;",condition_talk = "",},
	[400] = {id = 400,quest_id = 400,begin_talk = "",end_talk = "[@talk]npc_1§察哈台见识浅薄，此事还请冰豹兄指点迷津。;player§为今之计，只有用一个‘拖’字。;npc_1§可是……我们也只能拖上一时，过了这段时间他一样会逼我们做出决定。;player§时间可以改变一切，再说就算到时候仍旧没有转机。至少我们这些人可以获得充分的复元机会，拼上一死跟耶律赤眉杀一个鱼死网破，也比现在这样引颈待宰强上许多。;npc_1§主人果然没有说错，您审时度势的能力远在我等之上。;",condition_talk = "",},
	[401] = {id = 401,quest_id = 401,begin_talk = "",end_talk = "[@talk]npc_1§冰豹公子，外面有一位名翼虎的孩童和一位女子求见。;player§快请他们进来！;",condition_talk = "",},
	[402] = {id = 402,quest_id = 402,begin_talk = "",end_talk = "[@talk]npc_1§是你？你……可是那个在蛮州被俘的将领？;player§（暗自）原来她只记得蛮州的情形，我杀她的那段记忆仍然只是一片空白。;npc_1§你一直带着面具，没想到你居然就是冰豹。;player§谢谢你来看我。;npc_1§翼虎带我来得，你还是谢他吧。;",condition_talk = "",},
	[403] = {id = 403,quest_id = 403,begin_talk = "",end_talk = "[@talk]npc_1§冰豹叔叔，若是真想谢我，便将你的格斗术交给我一些。;player§完颜将军身为东胡四大名将，武功自然超群，小公子何必舍近求远？;npc_1§姐姐的那些花拳秀腿怎么能和冰豹叔叔的实战功夫相比，身为男子汉自然要学男人的功夫。;player§我答应你，不过，要先征得你姐姐的同意。;",condition_talk = "",},
	[404] = {id = 404,quest_id = 404,begin_talk = "",end_talk = "[@talk]npc_1§冰豹叔叔快救我！;",condition_talk = "",},
	[405] = {id = 405,quest_id = 405,begin_talk = "",end_talk = "[@talk]npc_2§如果不是前往墨先生那里，我还不知道你居然跑到这里胡闹！;player§小公子还小，完颜将军不可动用武力才好。。;npc_2§我偏偏不要跟那个墨老头子学习什么之乎者也，我要跟冰豹叔叔学格斗之术。;npc_2§跟我走！;npc_2§我不！看来别人说得都是真的。你根本不是我的亲生姐姐……呜呜呜……;",condition_talk = "",},
	[406] = {id = 406,quest_id = 406,begin_talk = "",end_talk = "[@talk]npc_1§呜呜呜……我是不是说错话，把姐姐气跑了？;player§翼虎不哭，我去帮你找找姐姐。;",condition_talk = "",},
	[407] = {id = 407,quest_id = 407,begin_talk = "",end_talk = "[@talk]npc_1§走开！我早已将这张桌子包下，你休要在这里纠缠，否则……;player§完颜将军这是要和在下较量一番了。;",condition_talk = "",},
	[408] = {id = 408,quest_id = 408,begin_talk = "",end_talk = "[@talk]player§在下有件事始终不明白，翼虎在你的心中是不是和这张桌子一样了也是属于你的私人之物呢？;npc_1§冰豹，翼虎向来民事，自从你出现以后他便处处和我作对，你究竟教唆他些什么？;player§如果你真的关心翼虎，便坐下，我们心平气和的商谈一下解决之道。;",condition_talk = "",},
	[409] = {id = 409,quest_id = 409,begin_talk = "",end_talk = "[@talk]player§完颜将军，翼虎这孩子性情倔强，他梦想成为驰骋沙场，你却强迫他学习兵法机械这些枯燥之术，却不知这恰恰引起了他的抵触之情。;npc_1§真想成为一代名将，又岂是单凭武力可以做到的。;player§翼虎年纪尚幼，他未必能够明白你的苦心，凡事皆要循序渐进，操之过急只能起到相反的作用。;",condition_talk = "",},
	[410] = {id = 410,quest_id = 410,begin_talk = "",end_talk = "[@talk]npc_1§我长年在外征战，对他的照顾自然疏忽了一些，想不到翼虎……竟然会恨我到如此的地步……;player§翼虎只不过是个孩子，他所说的那些，都是一时气话，你走后他已经开始后悔，只是抹不开这张面子，完颜将军可愿随我去乌氏行馆接他回去？;npc_1§我不去！;player§难道你们姐弟终生都不准备相见了？;npc_1§也罢！我跟你去接他。;",condition_talk = "",},
	[411] = {id = 411,quest_id = 411,begin_talk = "",end_talk = "[@talk]npc_1§冰豹叔叔，我……;player§是不是想起姐姐了？;npc_1§翼虎今天说了不该说的话，恐怕姐姐再也不会原谅我了。;player§我做个礼物给你，你带去向姐姐赔罪可好？;",condition_talk = "",},
	[412] = {id = 412,quest_id = 412,begin_talk = "",end_talk = "[@talk]npc_1§男子汉大丈夫，说出的话如同射出去的箭……;player§真正的大丈夫有所为有所不为，自己做错的事，说错的话，便要有勇气去承认，去面对，你这叫什么？伤害了自己的姐姐，居然都想逃避，不敢去面对，还想成为英雄好汉？;npc_1§我去向姐姐赔罪！;",condition_talk = "",},
	[413] = {id = 413,quest_id = 413,begin_talk = "",end_talk = "[@talk]npc_1§谢谢你！;player§区区小事，何足挂齿。;npc_1§听说你已经恢复了自由之身？不如教翼虎习武可好？;player§承蒙完颜将军看得起在下，我只好恭敬不如从命了。;",condition_talk = "",},
	[414] = {id = 414,quest_id = 414,begin_talk = "",end_talk = "",condition_talk = "",},
	[415] = {id = 415,quest_id = 415,begin_talk = "",end_talk = "[@talk]npc_1§您不必担心，我既然将乌氏家业转交给你，以后便会为你鞠躬尽瘁，死而后已。;player§可这件事并不容易。;npc_1§察哈台别无所求，只求乌氏能够保存下来，我愿为乌氏穷尽一生精力，至死不渝，主任答应！;player§从今日起，我在乌氏便在！;",condition_talk = "",},
	[416] = {id = 416,quest_id = 416,begin_talk = "",end_talk = "[@talk]npc_1§你帮我化解了和翼虎之间的矛盾，乌氏行馆的事情我会帮你。;player§耶律赤眉位高权重，这次的事情恐怕没有这么容易作罢。;npc_1§你将乌氏行馆的房地契约全部交给我，我会证明给耶律赤眉看，已经将乌氏的物业全部买了下来。;player§谢过完颜将军。;npc_1§未免此事暴露，先把行馆中的耶律亲兵除掉才好！;",condition_talk = "",},
	[417] = {id = 417,quest_id = 417,begin_talk = "",end_talk = "[@talk]npc_1§完颜将军派人传信，明日一早让主人去竞山王府。;player§什么事情？;npc_1§属下也不清楚，难道耶律赤眉对完颜将军施加了压力？;player§不排除这种可能性，我会见机行事。;",condition_talk = "",},
	[418] = {id = 418,quest_id = 418,begin_talk = "",end_talk = "[@talk]npc_1§你为何一直盯着我看，难道没听说过非礼勿视的道理？;player§世上美好之物本来便是供人欣赏的，否则又怎能体现她自身的价值？;npc_1§如今耶律赤眉带着国军前来，要降罪于你，你还有此等闲情逸致！;player§在下是否连累了将军？;npc_1§你还是先管好你自己吧。;",condition_talk = "",},
	[419] = {id = 419,quest_id = 419,begin_talk = "",end_talk = "[@talk]npc_1§今日喊你们来，并没有什么要事。秃颜将军刚刚献给我几只猛虎，云娜将军又新收了一名勇士，有缚虎搏龙之力，不知可否让我见识一下他的威风？;",condition_talk = "",},
	[420] = {id = 420,quest_id = 420,begin_talk = "",end_talk = "[@talk]npc_1§果然是勇猛异常，难怪完颜将军会对你如此欣赏。;player§耶律丞相，你究竟是什么意思？。;npc_1§没什么意思，只是赞赏你两句。;",condition_talk = "",},
	[421] = {id = 421,quest_id = 421,begin_talk = "",end_talk = "[@talk]npc_1§今日难得大家都这么开心，我和这位勇士下场搏击一下如何？;player§（内心）这完颜烈太醋意大发，该如何是好？;npc_1§怎么？你以为我不是你对手吗？;player§您乃是一国之君，万一……;",condition_talk = "",},
	[422] = {id = 422,quest_id = 422,begin_talk = "",end_talk = "[@talk]npc_1§国君深谙搏击之术，便是在国内也称得上顶尖高手，你放心，若是他一心伤你，我会出手阻止。;player§千万不可，否则便中了他们的圈套。;npc_1§可是你……;player§能得将军如此眷顾，在下便是死了也心甘情愿。;",condition_talk = "",},
	[423] = {id = 423,quest_id = 423,begin_talk = "",end_talk = "[@talk]npc_1§斗场之上，你不要把我当成国君，如果有意退让，吃亏的只有你自己。;player§陛下武功盖世，小人原非对手，甘愿认输！;npc_1§胜负未分。你这便认输了？;player§（暗自）事到如今，唯有受他一脚。;",condition_talk = "",},
	[424] = {id = 424,quest_id = 424,begin_talk = "",end_talk = "[@talk]npc_1§国君分明是受了耶律赤眉的挑唆，故意加害于你。你还痛吗？;player§有你在身边，便是再大的痛苦我也捱得住。;npc_1§若不是你有伤在身，我定要责罚你的轻狂无忌。;player§如今你因我得罪了陛下，怕是日子也会不好过，我打算带着所有乌氏行馆的人离开这里，不如你和我们一同离开！;npc_1§我还有翼虎，我不能带着他过着亡命天涯的日子。;",condition_talk = "",},
	[425] = {id = 425,quest_id = 425,begin_talk = "",end_talk = "[@talk]npc_1§我恨你！姐姐被陛下逼婚，而你却在此刻离她而去！;player§你说什么？;npc_1§她不只是为了翼虎，也是为了你，为了让陛下不追究你的罪责，让你们可以安然离开！;player§你放心，我一定会带着完颜将军，不！是带着你们一起离开！;",condition_talk = "",},
	[426] = {id = 426,quest_id = 426,begin_talk = "",end_talk = "[@talk]npc_1§师父！我知道你喜欢姐姐，姐姐也喜欢你，我不要姐姐嫁给那个混蛋国君，我要她嫁给你！;player§翼虎……;npc_1§姐姐被那个混蛋国君困在皇宫里，墨先生可以救她，快跟我走！;",condition_talk = "",},
	[427] = {id = 427,quest_id = 427,begin_talk = "",end_talk = "[@talk]npc_1§换好衣服后，咱们便从后门离开乌氏行馆，前往百步街的逸色戏园。;player§快低头，扮作搬运道具的脚夫。;",condition_talk = "",},
	[428] = {id = 428,quest_id = 428,begin_talk = "",end_talk = "[@talk]npc_1§你的令牌呢？;player§在这里在这里，您请看。;",condition_talk = "",},
	[429] = {id = 429,quest_id = 429,begin_talk = "",end_talk = "[@talk]npc_1§这是崇正殿，那边是逸宁宫，你们搬运道具就走这条路线。;player§是，公公。;npc_1§这里就是明天表演的场所，快把东西卸下来。记住，可别乱跑！;",condition_talk = "",},
	[430] = {id = 430,quest_id = 430,begin_talk = "",end_talk = "[@talk]npc_1§你在这里做什么？！;player§小的想去解个手，这皇宫太大迷路了，还请官兵大爷指个路。;npc_1§土包子！若不是皇上大婚，就凭你也想进来？去去去，茅房在那边。;",condition_talk = "",},
	[431] = {id = 431,quest_id = 431,begin_talk = "",end_talk = "[@talk]npc_1§一切都已经安排就绪，你们几个等到表演开始，便找机会进入祈福宫，中途已经有人负责接应。;player§多谢。;npc_1§尽量在婚礼之前制造混乱，好让云娜有脱身机会。我让人给你们准备好了几套衣服，应该很容易混进去。;",condition_talk = "",},
	[432] = {id = 432,quest_id = 432,begin_talk = "",end_talk = "[@talk]npc_1§衣服都在那里。;player§可焦镇期和狼刺都是满脸的胡须，哪里像是太监？;npc_1§你们还是快把胡须削光吧，有人会在御花园门前等你们。;",condition_talk = "",},
	[433] = {id = 433,quest_id = 433,begin_talk = "",end_talk = "[@talk]npc_1§你们几个不将礼品送往司礼处查收，来这里做什么？;player§我们……我们……;",condition_talk = "",},
	[434] = {id = 434,quest_id = 434,begin_talk = "",end_talk = "[@talk]npc_1§怎么？老佛爷的事情你们也要管吗？这些礼物是耶律丞相专门敬献给老佛爷的，是不是还要检查一下？;",condition_talk = "",},
	[435] = {id = 435,quest_id = 435,begin_talk = "",end_talk = "[@talk]npc_1§没有用的东西，呆在那里干什么，还不赶快进去。;player§是。;npc_1§左边的道路通往祈福宫，你们自己去吧。;player§有劳公公了。;",condition_talk = "",},
	[436] = {id = 436,quest_id = 436,begin_talk = "",end_talk = "[@talk]npc_1§主人，铁锹、撬棍和火种都在这里。;player§好，你和狼刺去放火，火势越大越好。;",condition_talk = "",},
	[437] = {id = 437,quest_id = 437,begin_talk = "",end_talk = "[@talk]npc_1§主子，这书架之处是中空的！可这条石将洞口完全嵌住，咱们要如何打开？;player§你看！这石块表面上有七个孔洞，这按照天罡北斗排列的七个圆孔就是开启断龙石的钥匙。;",condition_talk = "",},
	[438] = {id = 438,quest_id = 438,begin_talk = "",end_talk = "[@talk]npc_1§阿东唯恐不够混乱，又去养心殿放火去了，让我先回来告诉你们一声，主人不必等他。;player§云娜！慧乔！翼虎！你们终于逃出来了！;",condition_talk = "",},
	[439] = {id = 439,quest_id = 439,begin_talk = "",end_talk = "[@talk]npc_1§那个傻皇帝还在等着姐姐拜堂哩，咦！这就是机关么？;player§这条石之上的七个孔洞，是按照北斗七星的位置排列，我们按照从头至尾地顺序，先同时按下一三，而后按下二五七，最后再按下四六。;",condition_talk = "",},
	[440] = {id = 440,quest_id = 440,begin_talk = "",end_talk = "[@talk]npc_1§小心！;player§这洞不知道有多深啊，跳下去会不会摔死？;npc_1§嘘……有动静！他们应该已经发现了姐姐逃走的事情，很快就会搜到这里。;",condition_talk = "",},
	[441] = {id = 441,quest_id = 441,begin_talk = "",end_talk = "[@talk]npc_1§我先下去试试！;player§怎么样？;npc_1§主人！下面原来是个烂泥塘！松软软的难怪没有声音。;player§你们先下去，我把这里烧掉！;",condition_talk = "",},
	[442] = {id = 442,quest_id = 442,begin_talk = "",end_talk = "[@talk]npc_1§今日脱难多亏了多图尔公公。;player§那个多图尔公公究竟是什么人？;npc_1§当初我父王曾经救过他的性命，此人一直都铭记于心，如果没有他的照应，我又怎能轻易从宫中逃出。;player§我真害怕你被那个老乌龟纠缠住。;",condition_talk = "",},
	[443] = {id = 443,quest_id = 443,begin_talk = "",end_talk = "[@talk]npc_1§路越来越窄了。;player§这石门挡住了去路，按照地图上标记，这就是地下通道的第一处机关所在。;",condition_talk = "",},
	[444] = {id = 444,quest_id = 444,begin_talk = "",end_talk = "[@talk]npc_1§墨先生天文地理，占卜星象无所不通，先皇当初修建这条地道，就是为了逃生之用。;player§出去以后一定要好好感谢这位东胡的先皇，如果不是他留下这条秘道，我们焉能顺利的逃出戒备森严的黑沙城。;",condition_talk = "",},
	[445] = {id = 445,quest_id = 445,begin_talk = "",end_talk = "[@talk]npc_1§听到水声了！;player§这里是一个山洞，看来附近必有瀑布。;",condition_talk = "",},
	[446] = {id = 446,quest_id = 446,begin_talk = "",end_talk = "[@talk]npc_1§哈哈哈哈！天无绝人之路！可得好好洗个澡！;player§如此，咱们一起入水吧！;",condition_talk = "",},
	[447] = {id = 447,quest_id = 447,begin_talk = "",end_talk = "[@talk]npc_1§我……我不识水性……;player§哈哈哈！不要紧！我一个个把你们送出去。;",condition_talk = "",},
	[448] = {id = 448,quest_id = 448,begin_talk = "",end_talk = "[@talk]npc_1§小怜昨日便在这里安营扎寨，只等公子到来。诸位请更衣，我们要尽快离开这里。;",condition_talk = "",},
	[449] = {id = 449,quest_id = 449,begin_talk = "",end_talk = "[@talk]npc_1§巨灵，你去把那辆马车毁了，将马匹放了吧。;player§他是？;npc_1§公子，巨灵天生聋哑，虽然是我的仆从，可是从小跟我一起长大，和我的感情和兄弟无异，希望公子以后能够善待他。;player§诸葛兄放心，我会当他自己兄长一样看待。可是既然他天生聋哑，又怎会听懂你的话语呢？;npc_1§他能够读懂唇语。;",condition_talk = "",},
	[450] = {id = 450,quest_id = 450,begin_talk = "",end_talk = "[@talk]npc_1§翻过这座山，我们便离目的地不远了。从这里往上，温度会越来越低，按照我们现在的速度，估计再过两个时辰我们才可以抵达雪线。;player§可再有半个时辰就会天黑，我们不可能抹黑前进。;npc_1§大家也都累了。我们今夜就在那里宿营，明晨天亮继续出发。;",condition_talk = "",},
	[451] = {id = 451,quest_id = 451,begin_talk = "",end_talk = "[@talk]npc_1§公子不必担心我。;player§可诸葛兄为何不吃些饭？;npc_1§公子误怪，我向来喜欢独处，绝无其他意思。;",condition_talk = "",},
	[452] = {id = 452,quest_id = 452,begin_talk = "",end_talk = "[@talk]npc_1§这个先生乃是世外高人，岂会如同你一样，身上沾染着太多世俗之气。;player§却不知你所说的世俗之气指的又是什么？;npc_1§自然是吃喝嫖赌！;player§今日吃喝都占了，我还未嫖过呢，完颜大将军是不是来满足我一下。;",condition_talk = "",},
	[453] = {id = 453,quest_id = 453,begin_talk = "",end_talk = "[@talk]npc_1§公子还未安歇？;player§诸葛兄是不是还有牵挂？;npc_1§我本非东胡之人，这片土地更非我故土，我又有何留恋？;player§可是诸葛兄始终都在怅望黑沙城的方向……;",condition_talk = "",},
	[454] = {id = 454,quest_id = 454,begin_talk = "",end_talk = "[@talk]npc_1§我在为师父送行。师父五年前辞去东胡官职，潜居世外，便是感到自己罪孽深重，着五年来，他潜心修行，可是始终无法原谅自己。;player§无论如何墨先生都是因为我而殉难。;npc_1§师父只有这样才能得到真真正正的解脱。师父悟到一个道理，心中的仇恨越大对自己的伤害越大。;",condition_talk = "",},
	[455] = {id = 455,quest_id = 455,begin_talk = "",end_talk = "[@talk]player§这雕儿飞得好高，雪线之上很少有动物出没……;npc_1§难道是耶律赤眉的神兽营追来了？拿弓箭来！;",condition_talk = "",},
	[456] = {id = 456,quest_id = 456,begin_talk = "",end_talk = "[@talk]npc_1§这畜生飞得太高，此弓的强度恐怕不够。;player§敌人很快就能够得知我们的位置，大家加快速度，争取尽快翻越峰顶。;npc_1§敌人追上来了，并且行进速度奇快。;player§按照敌人现在的速度。应该很快就能够追上我们。;",condition_talk = "",},
	[457] = {id = 457,quest_id = 457,begin_talk = "",end_talk = "[@talk]npc_1§神兽营地这些人并不难对付，只是不知道鲁宫暴有没有来？鲁宫暴和墨无伤并称东胡两大奇人，鲁宫暴擅长兽语，可以驱使兽群。若是他亲来。恐怕……;",condition_talk = "",},
	[458] = {id = 458,quest_id = 458,begin_talk = "",end_talk = "[@talk]npc_1§雕群！;player§此地不宜留，赶快找地方藏起来！;npc_1§来不及了，拿起弓箭射准备战斗！;",condition_talk = "",},
	[459] = {id = 459,quest_id = 459,begin_talk = "",end_talk = "[@talk]npc_1§巨灵！将墨氏连弩发给大家！;player§射！;",condition_talk = "",},
	[460] = {id = 460,quest_id = 460,begin_talk = "",end_talk = "[@talk]npc_1§我们去山岩附近暂时躲避一下。;player§那边好像并没有道路可以前行。;npc_1§我并未说要继续前进，那里山岩中凹是一个藏身的好地方。;player§可猎犬的嗅觉极其灵敏，我们未必可以逃过它们的追踪。;",condition_talk = "",},
	[461] = {id = 461,quest_id = 461,begin_talk = "",end_talk = "[@talk]npc_1§那片雪坡角度极陡，上方有七彩光带，显然在坡顶布满冰川。以我们目前的行进速度很难摆脱神兽营的追踪，最好的办法就是利用地形将他们全部消灭！;",condition_talk = "",},
	[462] = {id = 462,quest_id = 462,begin_talk = "",end_talk = "[@talk]npc_1§巨灵，霹雳弹拿出来。;player§这是……？;npc_1§等到他们来到雪坡上，我们将霹雳弹射向坡顶冰川，爆炸必将引起一场天崩地裂的雪崩。;",condition_talk = "",},
	[463] = {id = 463,quest_id = 463,begin_talk = "",end_talk = "[@talk]npc_1§这弹弓的强度不够，恐怕射不到峰顶。;",condition_talk = "",},
	[464] = {id = 464,quest_id = 464,begin_talk = "",end_talk = "[@talk]npc_1§射！;",condition_talk = "",},
	[465] = {id = 465,quest_id = 465,begin_talk = "",end_talk = "[@talk]npc_1§那里是卡其雅！走过她，对面的雪丘就是凯拉尔山的顶峰。黎明之前我们就可以到达凯拉尔山的顶峰。;player§可这雪山之巅，怎么会有湖泊的存在？;npc_1§卡其雅的构成很奇怪，每到夏季的时候，日照延长，湖底就会涌出大量地热，使水温变暖，融化整个湖泊，其余的季节，都处于冰封状态。;player§这么说再过两个月，这里就会恢复碧波荡漾的景色？;",condition_talk = "",},
	[466] = {id = 466,quest_id = 466,begin_talk = "",end_talk = "[@talk]npc_1§听！似乎有东西正向我们这边飞速靠近！;player§野牛群……;npc_1§快跑！;",condition_talk = "",},
	[467] = {id = 467,quest_id = 467,begin_talk = "",end_talk = "[@talk]npc_1§来不及了！用霹雳弹炸开湖面！;player§你们快走，我留下来断后！;",condition_talk = "",},
	[468] = {id = 468,quest_id = 468,begin_talk = "",end_talk = "[@talk]npc_1§你们先走！不要管我！;player§不行！我怎能丢下你？;npc_1§你……为什么要这么傻……;player§我说过，永远不会将你放弃！慧乔！不要离开我！慧乔！;",condition_talk = "",},
	[469] = {id = 469,quest_id = 469,begin_talk = "",end_talk = "[@talk]npc_1§公子！你怎么样？;player§慧乔呢？！我要去看她！;npc_1§完颜将军正在为清清姑娘更换衣物……;player§慧乔……;",condition_talk = "",},
	[470] = {id = 470,quest_id = 470,begin_talk = "",end_talk = "[@talk]npc_1§我们越过前方的雪峰，就可以抵达了凯达拉尔山的东麓，山下丛林密布，而且都是游牧部落的势力范围，东胡人应该不会追击到这里。;player§你：收拾一下，我们尽快出发！;",condition_talk = "",},
	[471] = {id = 471,quest_id = 471,begin_talk = "",end_talk = "[@talk]npc_1§逃离了黑沙城，你好像并不开心，你还在担心清清？;player§嗯。;npc_1§说出来，也许我可以为你分担。;",condition_talk = "",},
	[472] = {id = 472,quest_id = 472,begin_talk = "",end_talk = "[@talk]player§清清的名字叫全慧乔，当初她背后的那一箭就是我所射！如果给我一个重来的机会，我决不会射出那一箭……;npc_1§不！如果回到过去，你仍然会射出那一箭，因为你的目的是天下，一个真正的王者有些时候，必须要牺牲感情作为代价。;player§你会不会因些而看不起我？;npc_1§我不会，我相信慧乔也不会，因为我们爱上的就是一个王者，而这种爱要付出比别人多得多的代价。;",condition_talk = "",},
	[473] = {id = 473,quest_id = 473,begin_talk = "",end_talk = "[@talk]npc_1§姐姐！师父！清清姐姐醒来了！;player§真的？！;",condition_talk = "",},
	[474] = {id = 474,quest_id = 474,begin_talk = "",end_talk = "[@talk]npc_1§你是谁？我好像并不认识你？;player§清清！是我啊！;npc_1§清清是谁？你又是哪一个？;player§清清，你记得我的，我们一起落入了水中……;",condition_talk = "",},
	[475] = {id = 475,quest_id = 475,begin_talk = "",end_talk = "[@talk]npc_1§放开我！你走开！我根本就不认识你，你快走开！;player§慧乔！;",condition_talk = "",},
	[476] = {id = 476,quest_id = 476,begin_talk = "",end_talk = "[@talk]npc_1§主人！山下有一个‘差赫尔伐木场’也是乌氏的产业，可以去差赫尔伐木场歇息。当年我和突藉便是在差赫尔伐木场中训练的。;player§好！今日我们便在那里休息。;",condition_talk = "",},
	[477] = {id = 477,quest_id = 477,begin_talk = "",end_talk = "[@talk]npc_1§主人，你终于来了！察哈台总管让我留在这里等候主人到来。木场的其他人都已经随队撤往苍白山林场。;player§你这些日子一直都候在这里吗？;npc_1§自从离开三鼎集，我已经在这里呆满了半个月。察哈台总管交代过，主人一日未到我就要在这里等上一日。;",condition_talk = "",},
	[478] = {id = 478,quest_id = 478,begin_talk = "",end_talk = "[@talk]player§云娜！你有没有见到慧乔？;npc_1§清清姐姐说去那边采些花朵。;player§她去了多少时候？;npc_1§半个多时辰的样子……;player§慧乔！慧乔！;",condition_talk = "",},
	[479] = {id = 479,quest_id = 479,begin_talk = "",end_talk = "[@talk]npc_1§慧乔并没有离开，一直在营地附近采参，别太担心了。;player§那就好。;npc_1§我觉得你也许应该和慧乔开诚布公的谈上一谈，继续这样下去只会彼此折磨。;player§慧乔根本就没有失去记忆，她现在的一切都是伪装出来的。;",condition_talk = "",},
	[480] = {id = 480,quest_id = 480,begin_talk = "",end_talk = "[@talk]npc_1§慧乔的伪装根本就是一种逃避，她不知道如何去面对你。当局者迷旁观者清，这两是慧乔唯独对你冷淡，枉你聪明一世，遇到这种事情竟然也糊涂了起来。;player§慧乔短时间内恐怕无法接受这样的现实。;npc_1§我倒不这么认为，她既然决定留下，内心中就已经接受了你，也许她现在就在等你主动开口。;",condition_talk = "",},
	[481] = {id = 481,quest_id = 481,begin_talk = "",end_talk = "[@talk]npc_1§放开我！我不认得你，更不是你说的什么慧乔！;player§你是，你知道我是谁，你清清楚楚记得过去的每一件事！给我一个机会，让我补偿曾经对你做过的一切。;npc_1§你错了，我并非恨你，我恨的是自己，为什么要爱上你，为什么要爱上一个亲手杀死自己的男人！;",condition_talk = "",},
	[482] = {id = 482,quest_id = 482,begin_talk = "",end_talk = "[@talk]npc_1§当我醒来看到你的时候，内心痛苦到了极点，可是无论我想尽一切办法去恨你。却总是想起你这……坏蛋的诸般好处……;player§慧乔……;npc_1§我几次想一声不响的走开，每到最后又犹豫起来，我……知道你对我的心意，害怕……你无法承受我离开的结局……;player§慧乔，当初我射杀你的时候，乃是形势所迫不得已而为之。此后一直在懊悔。;",condition_talk = "",},
	[483] = {id = 483,quest_id = 483,begin_talk = "",end_talk = "[@talk]npc_1§主人，你们总算回来了。从谷口向右便是乌氏林场，那边有几间木屋是我让工人赶工完成，里面有日常所需的一切，主人尽可安心的住在这里。;npc_1§总管费心了。;",condition_talk = "",},
	[484] = {id = 484,quest_id = 484,begin_talk = "",end_talk = "[@talk]npc_1§主人！出事了！;player§是不是东胡兵马追过来了？;npc_1§大康动用三十万大军入侵秦国！根据我掌握的消息，大康以索要质子为名发动的这次战争。;",condition_talk = "",},
	[485] = {id = 485,quest_id = 485,begin_talk = "",end_talk = "[@talk]npc_1§康、秦两国实力都在汉国之上，两虎相争必有一伤，所以这场战争的最后结果极可能是陷入彼此对峙的僵持。;player§等到两国真正陷入僵持之中，坐山观虎斗地大汉将成为最大的受益者，其他列国也在等着看笑话，秦康的实力下滑，势必让八国的格局发生巨大的改变。;",condition_talk = "",},
	[486] = {id = 486,quest_id = 486,begin_talk = "",end_talk = "[@talk]player§父皇的决断看似明智实则昏庸，在这个时候攻击秦国实在是一记昏招。;npc_1§看来主人返回中原的计划只能延后了。;player§这些日子，你多多留意战事的变化。如果有异常状况发生，马上及时向我汇报。;npc_1§主人放心，属下知道。;",condition_talk = "",},
	[487] = {id = 487,quest_id = 487,begin_talk = "",end_talk = "[@talk]npc_1§还在想着康秦之间的战事？;player§我想应该是回去的时候了。;npc_1§战争刚刚开始，如果你现在回去，大康岂不是没有了发起战事的理由？;player§这场战争持续不了太长的时间，我必须在两国陷入僵局的时候回到秦都。;",condition_talk = "",},
	[488] = {id = 488,quest_id = 488,begin_talk = "",end_talk = "[@talk]npc_1§回秦都？！;player§从哪里跃倒，就要从哪里爬起来，没有人比我更适合充当两国之间的桥梁。;npc_1§可是如果时机把握不好，秦国有可能拿你泄愤。;player§所以我不能有任何的差错，过早出现秦国会杀我，大康也会恼我，过晚出现，两国一量议和，这件大功也不会落在我的身上。;",condition_talk = "",},
	[489] = {id = 489,quest_id = 489,begin_talk = "",end_talk = "[@talk]npc_1§公子，我想先行返回大秦！;player§你和我们想到了一起。;npc_1§我想从陆路先行返回大秦，入秦后先行和唐昧等人取得联系，为公子准备好一切。;player§雨季到来之时，便是两国的战事最为艰苦的时候，我从水路返回，取道高丽前往济州。;npc_1§好！我找到唐昧之后，便在济州恭候公子的大驾。;",condition_talk = "",},
	[490] = {id = 490,quest_id = 490,begin_talk = "",end_talk = "[@talk]npc_1§主人对在下恩同再造……;player§回到大秦你想做什么？;npc_1§田某如今已经是一无所有，只想找到我的儿女，找个安逸的地方，了却残生。;",condition_talk = "",},
	[491] = {id = 491,quest_id = 491,begin_talk = "",end_talk = "[@talk]npc_1§主人想我怎么做？;player§我要你将燕兴启的一切罪证全部交给我。;npc_1§我……真地不想继续招惹此人。;player§对你来说。这件事只不过是举手之劳，如果你帮我做成此事。我可以将你全家带回大康，燕兴启就算想对付你，恐怕到时候也是鞭长莫及。;",condition_talk = "",},
	[492] = {id = 492,quest_id = 492,begin_talk = "",end_talk = "[@talk]npc_1§我特来邀请公子观星。;player§诸葛兄可否为我卜算一下此行的凶吉？;npc_1§这几日我勤观天象，今年的雨季可能会提早到来，如果雨季提前到来，就意味着秦康之间的战事会提前进入僵局。这大雨对公子来说是一场甘霖。;",condition_talk = "",},
	[493] = {id = 493,quest_id = 493,begin_talk = "",end_talk = "[@talk]player§明天一早我们便坐船离开这里。;npc_1§主人放心，这艘船常年往返于月罗城和济州之间，船主信誉很好，不会有任何的问题。;player§我们离开东胡以后，乌氏的一切就交给你了，耶律赤眉肯定还会调查乌氏的产业，你要加倍小心。;npc_1§我会将乌氏的产业尽快转卖，等做完这件事，我就带着手下前往中原去投奔主人。;",condition_talk = "",},
	[494] = {id = 494,quest_id = 494,begin_talk = "",end_talk = "[@talk]npc_1§我想再多看几眼故土。;player§你在高丽还有没有亲人？;npc_1§我是孤儿，自小便被做御医的义父收养，义父也在三年前病逝，慧乔在这世上已经再也没有亲人了……;player§你还有我，今生今世我都不会离开你。;",condition_talk = "",},
	[495] = {id = 495,quest_id = 495,begin_talk = "",end_talk = "[@talk]npc_1§我离开中原时还不懂事，现在对于中原的那点印象早已模糊了。;player§诸葛兄踏入故土之后，也许会将过去的一点一滴重新回忆起来。;npc_1§墨氏和诸葛家的坟冢恐怕都已经芳草萋萋了。;player§我回到大康之后，一定为你们两家洗清冤屈，重新修葺你们的祖坟。无论付出再大的代价，我也要做成此事。;",condition_talk = "",},
	[496] = {id = 496,quest_id = 496,begin_talk = "",end_talk = "[@talk]npc_1§这片区域雾大，定要小心行驶。;player§咦，我们周围什么时候多了这些奇怪的船？;",condition_talk = "",},
	[497] = {id = 497,quest_id = 497,begin_talk = "",end_talk = "[@talk]npc_1§不好！快快避开！我们的船体比那艘帆船略小，若是被它正面撞上，船只恐怕会损毁。;player§莫不是遇上海盗？;",condition_talk = "",},
	[498] = {id = 498,quest_id = 498,begin_talk = "",end_talk = "[@talk]npc_1§公子！底……舱进水了，这船恐怕……要保不住了……;player§是海龙帮的人！果然遇到了海盗。;",condition_talk = "",},
	[499] = {id = 499,quest_id = 499,begin_talk = "",end_talk = "[@talk]npc_1§这船就快要沉了，把财务和女人尽快带过去。如果你不想其他人送命的话，最好不要轻举妄动。;player§可否打个商量？;npc_1§你？！你可是大康平王龙胤空？;",condition_talk = "",},
	[500] = {id = 500,quest_id = 500,begin_talk = "",end_talk = "[@talk]npc_1§这艘船就快要沉了，还是到我的船上说话。我问你，你究竟是不是平王？;player§我是。;npc_1§我早就知道你没有那么容易死掉。当年你从大康前往秦国为质的时候，我便拦截过你，没想到过去的一幕居然在我们身上重演。;",condition_talk = "",},
	[501] = {id = 501,quest_id = 501,begin_talk = "",end_talk = "[@talk]npc_1§此地风景甚好，真是人间天堂。;player§你喜欢这里么？;",condition_talk = "",},
	[502] = {id = 502,quest_id = 502,begin_talk = "",end_talk = "[@talk]npc_1§这位姑娘说得不错，这个岛的名字本身就是天堂岛，昨夜你经过的海港名叫月牙湾！;player§这些名字起得都是如此雅致，是不是邱兄的手笔？;npc_1§我可没有那样的本领，我们岛上除了月牙湾以外，还有七处名胜。;player§这位紫凝姑娘如此高才，有机会一定要认识一下。;",condition_talk = "",},
	[503] = {id = 503,quest_id = 503,begin_talk = "",end_talk = "[@talk]npc_1§岛主和紫凝姑娘来了！;",condition_talk = "",},
	[504] = {id = 504,quest_id = 504,begin_talk = "",end_talk = "[@talk]npc_1§邱大哥告诉我在海上劫到了一位故人，没想到居然又是你。;player§原来是姑娘，xx还要多谢你上次不杀之恩。;",condition_talk = "",},
	[505] = {id = 505,quest_id = 505,begin_talk = "",end_talk = "[@talk]npc_1§都说平王殿下已战死在蛮洲大战之中，没想到你却带着红颜知己在海上逍遥。;player§紫凝姑娘恐怕误会了，我们这次并非是为了游玩逍遥，而是为了返回秦都。;npc_1§平王不知道康秦正在大战吗？;",condition_talk = "",},
	[506] = {id = 506,quest_id = 506,begin_talk = "",end_talk = "[@talk]npc_1§平王为何不返回大康却选择大秦呢？;player§从东胡若想直接进入大康，必须翻越天岭，相比之下，还是海路更为安全……;npc_1§平王打算何时离开这里？;player§越快越好。;",condition_talk = "",},
	[507] = {id = 507,quest_id = 507,begin_talk = "",end_talk = "[@talk]npc_1§如果从这里走，最安全的路线就是南行取道中山、大汉、返回大康。;player§xx打算先回秦都！;npc_1§什么？康秦两国正在交战，你此时回去和送死又有何区别？;player§康秦之战已经让两国深陷泥潭之中，如果继续发展下去，只会让两国的国力不断衰落。;npc_1§平王为挽救天下苍生，而甘心身涉险境，乔某深感佩服。;",condition_talk = "",},
	[508] = {id = 508,quest_id = 508,begin_talk = "",end_talk = "[@talk]npc_1§平王放心，乔某冲着你的这方悲天悯人的大义，必定将殿下安全送达大秦。;player§只要能让百姓免于战火，xx就算搭上这条性命也在所不惜！;",condition_talk = "",},
	[509] = {id = 509,quest_id = 509,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下！您总算回来了！您可以暂时……先住在我的府上……;player§我已经有了一个绝佳的去处。;npc_1§万花楼？平王高明，万花楼的确是一个藏身的好地方，我马上替您安排。;",condition_talk = "",},
	[510] = {id = 510,quest_id = 510,begin_talk = "",end_talk = "[@talk]npc_1§按您之前的吩咐，我今天进宫，可若我见到太后，该安排她到何处见您？;player§如果她真有诚意，应该会屈尊移驾。;npc_1§这……恐怕我说出来，太后就会要了我的脑袋。;player§你把信呈给太后，她一定会来。;",condition_talk = "",},
	[511] = {id = 511,quest_id = 511,begin_talk = "",end_talk = "[@talk]npc_1§去外面等我！;player§太后请坐。;npc_1§你变了！变得哀家几乎认不出你了。;player§北疆的风霜和严寒足以改变任何人。;npc_1§如果一切可以重来，我决不会让你去北疆参战。;",condition_talk = "",},
	[512] = {id = 512,quest_id = 512,begin_talk = "",end_talk = "[@talk]npc_1§你还在怪我？;player§今日我请你来这里相聚并不是为了过去，而是为了现在。太后如果不想这场战争继续下去，我可以充当两国议和的使者。;npc_1§议和？你果然会替康国考虑。歆德皇借口为你复仇挑起这场战争，搞得两国血流成河，现在你居然完好无恙的回来了，议和？我如何对得起死去的数万将士！;",condition_talk = "",},
	[513] = {id = 513,quest_id = 513,begin_talk = "",end_talk = "[@talk]npc_1§你为何不说话？;player§我还以为这世上，太后是最了解我的人。我仿佛看到了蛮州大战的重演，借助外力来对付敌人。不同在于上次白晷是内乱，这次的大康却是外敌。;npc_1§你……;player§除掉白晷，损失掉大秦的三座重镇和十余万将士，除掉大康又将失去什么？多少土地？多少士后？太后地心里已经做足了准备了吗？;",condition_talk = "",},
	[514] = {id = 514,quest_id = 514,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，我为您准备好了需要的一切。已经差人送往枫林阁，你在秦都的一切活动所需都包在我的身上。;player§钱兄，xx永远不会忘记你这个朋友。;npc_1§殿下，这两日恐怕拜访你的人会很多。;player§会越来越多的。;",condition_talk = "",},
	[515] = {id = 515,quest_id = 515,begin_talk = "",end_talk = "[@talk]npc_1§公子，您回来了！;player§我们回去吧，大家都在等着，孙先生呢？;npc_1§孙先生病了，在西厢房养病呢。;player§快带我去见他。;",condition_talk = "",},
	[516] = {id = 516,quest_id = 516,begin_talk = "",end_talk = "[@talk]npc_1§孙先生在你走后不久便病了，我本以为他乃是当世神医，这点小病应该不成问题，可是没想到他的病情越来越严重，三月前又突然中风，连话都说不出来了。;player§孙先生，孙先生！;npc_1§公子，慧乔姑娘她们应该在这几日抵达秦都，也许会有救治孙先生的办法。;",condition_talk = "",},
	[517] = {id = 517,quest_id = 517,begin_talk = "",end_talk = "[@talk]npc_1§我刚才察看过孙先生的脉象，他不仅仅只是中风这么简单……;player§究竟怎么回事？;npc_1§孙先生的体内还中了剧毒。根据我的推算，他中毒的时间已经很长，如今毒性已经深入肺腑，恐怕天间再也无人有回天之力。;player§慧乔，不管用什么方法，你都要让孙先生清醒过来。我要让他亲口告诉我，究竟是谁害的他！;",condition_talk = "",},
	[518] = {id = 518,quest_id = 518,begin_talk = "",end_talk = "[@talk]npc_1§太子……你……回来了……;player§孙先生……是我……我回来了……;npc_1§你……终于活着回来了……我终于……等到了你……我……有话……要单独对你说……;",condition_talk = "",},
	[519] = {id = 519,quest_id = 519,begin_talk = "",end_talk = "[@talk]npc_1§太子龙胤基才是……你的亲生父亲……歆德皇是……;player§你在骗我！;npc_1§你……母亲入官……之前便和……你的父亲有过宿缘……歆德皇……因为垂涎你母亲的美色，而强行将你母亲霸占……;",condition_talk = "",},
	[520] = {id = 520,quest_id = 520,begin_talk = "",end_talk = "[@talk]npc_1§你……父亲……临终之前托付我，如果你……是个胸怀大志的孩子，便将这幅……地图交给你。;player§若我无所作为呢？;npc_1§如果你庸碌无为……贪享安逸……便永远不要告诉……你这件事……让你在平平安安中……渡过此生……;",condition_talk = "",},
	[521] = {id = 521,quest_id = 521,begin_talk = "",end_talk = "[@talk]npc_1§皇位本来就属于你的父亲……你……比任何人都有资格去……继承大康……你要拿回属于……你的一切……;player§孙先生，你放心，我很快就会返回大康。;npc_1§你离去……之后，太后突然宣我进宫……让我帮她……;player§果然是她！;npc_1§此事怪不得……她……我知道……了她的秘密……她不杀我……那才奇怪……，我只是担心她会对……公子不利……;",condition_talk = "",},
	[522] = {id = 522,quest_id = 522,begin_talk = "",end_talk = "[@talk]npc_1§心里难受……就哭出来吧。;player§我何时难受？我又怎会难受？;npc_1§孙先生……已经过世了……;",condition_talk = "",},
	[523] = {id = 523,quest_id = 523,begin_talk = "",end_talk = "[@talk]npc_1§皇侄！你受苦了……;player§八皇叔有什么话，尽管对xx明言。;npc_1§好，在你面前我也没有什么可以隐瞒的，你父皇年事已高，处理政事已经是心有余而力不足，这次的秦康之战，便是他所出的一个昏招，册立太子之事已如箭在弦上不得不发。;",condition_talk = "",},
	[524] = {id = 524,quest_id = 524,begin_talk = "",end_talk = "[@talk]npc_1§皇侄，此次我冒险前来秦都就是为了一件事……我特来送给皇侄一份大礼！;player§噢？;npc_1§此次议和所有的功劳都会是你的，任何人不会抢走你的风光，经此一事，你在国内百姓心中的声望会超出任何一位皇子皇孙！;player§皇叔！我只想早日回到大康，安安稳稳的做我的平王。;",condition_talk = "",},
	[525] = {id = 525,quest_id = 525,begin_talk = "",end_talk = "[@talk]npc_1§你终归还是对我有戒心，也罢！日后你定然会知道我的心意，我只需要你答应我一件事，将来你若成为大康皇帝，需得保我一生平安！;",condition_talk = "",},
	[526] = {id = 526,quest_id = 526,begin_talk = "",end_talk = "[@talk]npc_1§贤弟找我有事？;",condition_talk = "",},
	[527] = {id = 527,quest_id = 527,begin_talk = "",end_talk = "[@talk]npc_1§贤弟这是什么意思？;player§我烧掉这本帐簿是想告诉你，这帐册对我根本无关紧要，我们并不是敌人，至少现在不是。既然如此，我们为什么不好好的合作呢？;",condition_talk = "",},
	[528] = {id = 528,quest_id = 528,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，太后在凤阳宫设下酒宴，为您送行！;player§深夜入宫恐怕会遭人话柄，许公公还是替我谢谢太后吧。;npc_1§太后说平王一定会记得昔日的母子情分。;player§好吧！引路！;",condition_talk = "",},
	[529] = {id = 529,quest_id = 529,begin_talk = "",end_talk = "[@talk]npc_1§你为何不喝酒？是不是以为我在酒中下毒？算了，你喝也罢，不喝也罢，我不会勉强你……你很有本事，居然能将和约的事情圆满解决。;player§既然是和谈，就必须双方公平，不可以插手任何一方地利益。;",condition_talk = "",},
	[530] = {id = 530,quest_id = 530,begin_talk = "",end_talk = "[@talk]npc_1§我今日召你过来，就是想告诉你，我当初并无害你之意，有些事情……;player§这是你当年给我的龙佩，现在还你。;npc_1§你果然非同凡响，你再不是一个质子，自然不会将我这个曾经的母后放在眼里。;player§太后，你喝醉了，xx告辞！;",condition_talk = "",},
	[531] = {id = 531,quest_id = 531,begin_talk = "",end_talk = "[@talk]npc_1§混帐东西，只要我想杀你，随时可以将你置于死地！;player§您是高高在上的秦国太后，我这样一个质子当然不会让你放在眼里。只要你需要可以对我呼之即来挥之即去，也可以随时像踩死一只蚂蚁一样夺取我的生命。;npc_1§你……你给我滚出去！;player§你为什么不喊人来杀我？;npc_1§我的忍耐是有限度的…;",condition_talk = "",},
	[532] = {id = 532,quest_id = 532,begin_talk = "",end_talk = "[@talk]npc_1§平王……只有平王殿下还记得来拜祭亡父。;player§白大将军精忠报国，死而后已，是我辈的楷模。;npc_1§你好像有心事？;player§我明日就要离开这里了，临别之时总觉得有件事放心不下。;",condition_talk = "",},
	[533] = {id = 533,quest_id = 533,begin_talk = "",end_talk = "[@talk]npc_1§什么事情？;player§当日在战场之上，白大将军让我照顾你一生一世。;npc_1§这句话……当真是我爹爹说的？;player§千真万确！;",condition_talk = "",},
	[534] = {id = 534,quest_id = 534,begin_talk = "",end_talk = "[@talk]npc_1§可是……姐姐已经为我订下了一门亲事！姐姐将许配给太仆朱无墨的三公子朱自真。;player§你根本就没有见过那个朱自真，又怎会对他有任何感情存在？;npc_1§这世上我只有姐姐这个唯一的亲人，我如果不听她的话，她肯定要伤心的。;player§难道就为了听她的话就要跳入火坑吗？也许在她的心中早已不把你当成她的妹妹。;",condition_talk = "",},
	[535] = {id = 535,quest_id = 535,begin_talk = "",end_talk = "[@talk]player§我只想问你一句话，在你心中究竟喜不喜欢我？;npc_1§就算你知道我的心意又如何？我不可以这样一走了之，更不可以对不起姐姐。;player§你有没有想过自己，有没有考虑过我的感受？;",condition_talk = "",},
	[536] = {id = 536,quest_id = 536,begin_talk = "",end_talk = "[@talk]npc_1§xx……你不可以辜负我。;player§我会和你一生一世相守在一起，随我一起返回大康好吗？;npc_1§我跟你就这样不声不响的离开，姐姐该如何向朱家交代？;player§明日一早我便来接你，离开大秦的事情不要告诉任何人，以后有机会再向你姐姐解释。;",condition_talk = "",},
	[537] = {id = 537,quest_id = 537,begin_talk = "",end_talk = "[@talk]npc_1§看到你心事重重，特地想来为你分担一下。;player§明日我们就能够抵达大康的水域。我人虽然未到大康，可是早有人做好准备要刺杀我。;npc_1§木秀于林风必摧之，你这此的功绩举国百姓有目共睹，遭到其他皇子的嫉妒是理所当然的。;player§身为皇族，生活得未必如普通百姓安逸自在。;npc_1§每个人的追求不同，如果给你闲云野鹤的生活，你肯定不开心。;",condition_talk = "",},
	[538] = {id = 538,quest_id = 538,begin_talk = "",end_talk = "[@talk]npc_1§公子进入大康以来，所到之处百姓无不夹道欢迎。;player§是啊，我虽欢喜，却也头痛。;npc_1§我们此刻虽然没有进入康都，可是消息恐怕已经传到了城内，若是再出现夹道欢迎的混乱场面，公子的安全将受到空前的挑战。;",condition_talk = "",},
	[539] = {id = 539,quest_id = 539,begin_talk = "",end_talk = "[@talk]npc_1§城内道路两旁全都站满了欢迎平王归来的百姓，有的已经等了整整一夜。;;player§我们今晨方到，中途还故意担搁了一天，何以百姓会如此清楚我们的行程？此事是不是有人故意安排？;npc_1§公子不必担心，我和焦大哥会保护在你的左右！;",condition_talk = "",},
	[540] = {id = 540,quest_id = 540,begin_talk = "",end_talk = "[@talk]npc_1§平王千岁！;player§起来吧！;npc_1§平王知不知道，你已经是大康百姓的希望所在……;player§xx必将振兴祖宗的这份家业，带给万民幸福安康。;",condition_talk = "",},
	[541] = {id = 541,quest_id = 541,begin_talk = "",end_talk = "[@talk]npc_1§有杀气！殿下小心！;",condition_talk = "",},
	[542] = {id = 542,quest_id = 542,begin_talk = "",end_talk = "[@talk]npc_1§这还了得？！居然敢在康都之中公然刺杀皇侄，若是让我查出是谁所为。一定将他碎尸万断。;player§皇叔不必追究了，即便是查出真相也无法证实。皇叔，我受了伤，此时入宫去见父皇，恐怕只会令他担心……;npc_1§既然如此，还是由我先入宫向陛下禀明一切，你先回平王府休息。;",condition_talk = "",},
	[543] = {id = 543,quest_id = 543,begin_talk = "",end_talk = "[@talk]npc_1§小主人…小主人…你总算回来了。这几年我们无时无刻不在盼着小主人回来，上天眷顾，果然让小主人平安归来。;player§我们重逢是件大喜事，为何总是哭个不停？;npc_1§我们是太高兴了。;player§你们放心，明日我就会入宫面见父皇，把你们两个要到王府内。;npc_1§小主人恐怕还不知道，陛下已经恩准我们离开皇宫，来到小主人身边伺候您。这次多亏了雍王向陛下求情。;",condition_talk = "",},
	[544] = {id = 544,quest_id = 544,begin_talk = "",end_talk = "[@talk]npc_1§小主人，有人托我向你捎句话儿！她让我告诉小主人，有些事，有些人，你最好还是当作永远没有发生过，永远没有见过。;player§她……还好吗？;npc_1§小主人离开大康之后，很多事情都发生了改变，珍妃娘娘也已经重新获得陛下的宠幸，去年已经被册封为贵妃……;player§够了！;",condition_talk = "",},
	[545] = {id = 545,quest_id = 545,begin_talk = "",end_talk = "[@talk]npc_1§皇侄，这是我从高丽购入的山参，给你补一补身子。陛下十分关心你的伤情，让你不必急于去见他，安心留在王府中养病。;player§多谢皇叔了。;",condition_talk = "",},
	[546] = {id = 546,quest_id = 546,begin_talk = "",end_talk = "[@talk]npc_1§这帮刺客误打误撞居然帮了公子一个大忙，至少现在我们有充分的时间可以去了解大康的局势。;player§陈先生这一天的功夫可曾了解到什么？;npc_1§公子忙着会见你的诸位皇兄皇侄，子苏闲着无事和易安聊了一个下午。公子初返国内，一切都是重新开始，首先就是要站稳脚跟。;",condition_talk = "",},
	[547] = {id = 547,quest_id = 547,begin_talk = "",end_talk = "[@talk]player§孩儿xx拜见父皇万岁，万岁，万万岁！;npc_1§过来，让我好好看看。;这几年你受苦了……;player§只要能换得大康安宁，孩儿吃的这点苦算不了什么。;",condition_talk = "",},
	[548] = {id = 548,quest_id = 548,begin_talk = "",end_talk = "[@talk]player§这雪参是苍白山雪峰所得，是不可多得的珍品，父王可用来进补。;npc_1§不可！三十一弟，父皇血热体虚，焉能再服用人参，你究竟是什么意思？;",condition_talk = "",},
	[549] = {id = 549,quest_id = 549,begin_talk = "",end_talk = "[@talk]npc_1§xx并非是存心害我，他也是一片孝心。;player§十三皇兄还没有看清此参的品相，怎可轻易断言呢？父皇不如传照御医，让他品评一下。;",condition_talk = "",},
	[550] = {id = 550,quest_id = 550,begin_talk = "",end_talk = "[@talk]npc_1§好参！陛下，此乃是万里挑一的雪参，中原地区绝对无法觅得，此参生自雪山之中，性凉质清，刚好可以用于清除陛下体内的虚火。;player§孩儿蛮州大战之后，流浪东胡，途径苍白山之时发现了这株雪参，便采来想日后敬献给父皇。;npc_1§这种品相的雪参往往生活在极寒之地，平王殿下采摘的时候想来费了不少辛苦。;player§孩儿这么多年不能守在父皇身边已属不孝。;只要能换得父皇康健，费了这点辛苦又算什么？;",condition_talk = "",},
	[551] = {id = 551,quest_id = 551,begin_talk = "",end_talk = "[@talk]npc_1§我的这些皇儿之中，属你吃得苦头最多，所以我也想对你有所补偿，你的婚事我一定要亲自替你操办。;player§谢父皇！;npc_1§你娶多少侍妾，我不会过问，不过纳妃之事非同小可，必须讲究门当户对，自从听到你从秦都启程的消息，我就让珍妃着手办理此事。;",condition_talk = "",},
	[552] = {id = 552,quest_id = 552,begin_talk = "",end_talk = "[@talk]npc_1§大臣们听说是平王纳妃，一个个争先恐后的把女儿送来。我这几日都快挑花眼了，千挑万选才挑出了这十二个。;player§孩儿一时间也难以做出判断，恳请父皇能给我两天的时间考虑。;",condition_talk = "",},
	[553] = {id = 553,quest_id = 553,begin_talk = "",end_talk = "[@talk]npc_1§珍妃，你代我送送xx。;player§不必劳驾娘娘。;npc_1§我刚好还有些事情私下对你说。;",condition_talk = "",},
	[554] = {id = 554,quest_id = 554,begin_talk = "",end_talk = "[@talk]npc_1§陛下有意让你去宣城。陛下内心中戒心早生，本来他是想你一回来就去宣城受封。纳妃之事是我所提起，只有用这个办法才能让你在康都占有一席之地。;player§他若一心想将我放逐到宣城，即便是纳妃也很难令他改变。;",condition_talk = "",},
	[555] = {id = 555,quest_id = 555,begin_talk = "",end_talk = "[@talk]npc_1§这名单上的人选，是我费了一番心血挑选，其中有两位无论是家世还是样貌都是上上之选。;player§哦？;npc_1§你只要能够顺利娶得其中一位为妻，便意味着你获得了一份强有力得支持，夫凭妻贵，在大康诸位皇子之中的地位会迅速得以提升。;",condition_talk = "",},
	[556] = {id = 556,quest_id = 556,begin_talk = "",end_talk = "[@talk]npc_1§左玉怡，当今相国左逐流的女儿；另一位便是翼王林悲天的宝贝女儿林楚儿。;player§林楚儿？;npc_1§翼王是西宫静德妃的亲弟弟，兴王龙胤滔的舅父，他和陛下又是表亲，多年来一直为陛下东征西战，立下战功无数，是歆德皇最为信任的臣子之一。;player§林楚儿我曾经见过，她好像只是一个孩子。况且翼王只有这一个女儿，他会愿意么？",condition_talk = "",},
	[557] = {id = 557,quest_id = 557,begin_talk = "",end_talk = "[@talk]npc_1§对主人帮助大的，说服她们家人的难度也是越大，听闻珍妃虽然已经托人向左逐流和林悲天转达了陛下的意思，不过他们仍然没有表态。;player§这次的选妃实际上是在选择利益，我定要好好考虑一下。;",condition_talk = "",},
	[558] = {id = 558,quest_id = 558,begin_talk = "",end_talk = "[@talk]npc_1§听闻翼王向来重情，四十二岁才得到了这个女儿，王妃因高龄产女难产而死，翼王再未有过纳妃之念，专心抚养这个女儿。;",condition_talk = "",},
	[559] = {id = 559,quest_id = 559,begin_talk = "",end_talk = "[@talk]npc_1§翼王只有一个女儿，林楚儿对他的影响力极大，如果公子娶她为妃，翼王必定会将对女儿的那份爱护转嫁到你的身上，对你以后的大计肯定会有帮助。;player§等我回头征求云娜她们的意见，再把纳妃的事情定下来。;",condition_talk = "",},
	[560] = {id = 560,quest_id = 560,begin_talk = "",end_talk = "[@talk]npc_1§我件事你无需问我，当日在凯拉尔雪峰之上我说曾经说过，我们爱上的就是一个王者，而这种爱要付出比别人多得多的代价……;player§云娜，我娶林楚儿更多的是为政治利益上的考虑，在我的心中她远远比不上你们的位置。;npc_1§我知道。我毕竟是一个女人，女人往往都是很小气的。;player§你在吃醋么？不如这样，我今晚便娶你们几个！;",condition_talk = "",},
	[561] = {id = 561,quest_id = 561,begin_talk = "",end_talk = "[@talk]npc_1§xx，你的眼光果然不错，翼王的这个女儿姿色出众，的确可以用倾国倾城可以形容。;player§父皇，儿臣听说……翼王对女儿极为宠爱，纳妃之事是否征求过他的意见？;npc_1§朕乃是大康之君，你是我的皇儿，论到身世地位，他有什么拒绝的理由？此事就这么定了，回头让他们挑选一个黄道吉日为你纳妃！;player§谢父皇！;",condition_talk = "",},
	[562] = {id = 562,quest_id = 562,begin_talk = "",end_talk = "[@talk]npc_1§我赐给你地那片领地，今年的课税仍然没有上缴，部分乱民借机作乱，你成婚以后，尽快还是前往宣城去处理一下。;player§是。;npc_1§这次你促成秦康两国和解，表面上为大康立下大功，可是你的很多兄弟却因此对你产生了忌恨，我并非是不想把你留在身边，让你出去历练，也对你有莫大的好处。;player§多谢父皇对孩儿的关怀。;",condition_talk = "",},
	[563] = {id = 563,quest_id = 563,begin_talk = "",end_talk = "[@talk]npc_1§二位王爷来得如此凑巧，我刚好备好了几味小菜，如果不嫌菜薄酒劣，便一起饮上几杯。;player§看来xx来得不是时候，打扰了皇叔的雅兴。;npc_1§平王为大康免去一场战火，本王早有意宴请，就怕平王不赏本王这个薄面。;",condition_talk = "",},
	[564] = {id = 564,quest_id = 564,begin_talk = "",end_talk = "[@talk]npc_1§平王小的时候，本王还抱过你来着，我记得那个时候，你的书法便写得相当不错了。;player§雕虫小技，不值一提。舅舅，xx这次前来特地给您带来了一件礼物。;npc_1§你既然称我一声舅父，我们之间又何须如此客套？;",condition_talk = "",},
	[565] = {id = 565,quest_id = 565,begin_talk = "",end_talk = "[@talk]npc_1§多谢平王，这……是本王收到的最珍贵的礼物。;player§舅父喜欢就好。;npc_1§适才本王睹物思人。想起亡妻，两位不要见笑。;player§舅父对舅母情深意重，实在是令人敬佩。;",condition_talk = "",},
	[566] = {id = 566,quest_id = 566,begin_talk = "",end_talk = "[@talk]npc_1§本王一生之中只爱过王妃一人，只可惜天意弄人，让我们阴阳相隔，生离死别……;player§多情自古伤别离，人世之中又有哪件事是真正完美的呢？;npc_1§来，难得我们这么投缘，干上一杯。;",condition_talk = "",},
	[567] = {id = 567,quest_id = 567,begin_talk = "",end_talk = "[@talk]npc_1§陛下今年已是古稀之年，至今太子之位仍然悬而未决，不知道平王有什么高见？;player§xx认为，其实谁做太子都没有关系，最主要的是能够真真正正的为大康着想，能彻底改变大康国力逐年减退的现实。;npc_1§平王以为谁有这种能力呢？;",condition_talk = "",},
	[568] = {id = 568,quest_id = 568,begin_talk = "",end_talk = "[@talk]npc_1§xx，既然你对本王如此坦诚，我也不妨以诚相待。你今日来的目的我很明白，实不相瞒，本王下午便去了皇宫，将我的本意告知了静德娘娘。;player§原来如此。;npc_1§我并不想让楚儿嫁入帝王之家，我为官多年，对皇族所发生的一切早就了如指掌，我不想将唯一的女儿送入火坑之中！;player§舅父的意思xx已经完全明白，你放心我明日便去宫中请父皇收回成命，xx决不会让舅父为难。;",condition_talk = "",},
	[569] = {id = 569,quest_id = 569,begin_talk = "",end_talk = "[@talk]npc_1§今晚和你的一席深谈，却让本王改变了看法，你的确是一位有心之人，我相信楚儿若是嫁给你，决不会委屈了她。本王只想要你一个承诺。;player§舅父请讲！;npc_1§我要你今生今世都要爱护楚儿，无论大康将来的局势如何发展，你都要保护她的安全。;player§舅父放心，xx一定不负您的嘱托。;npc_1§你去吧，明日一早我会亲自去叩见陛下。;",condition_talk = "",},
	[570] = {id = 570,quest_id = 570,begin_talk = "",end_talk = "[@talk]npc_1§恭喜公子，贺喜公子！还有一事，我刚才和完颜将军研究过，陛下所赐给公子的宣城虽然偏僻，可是地域倒是十分的广阔。;player§宣城地处大康地北疆，虽说距离康都只有三日的路程，可是地势偏高，地貌复杂，谷物产量极低，北方有阴山，南方却濒临曲沽河，地广人稀……;npc_1§宣城西侧当年乃是北胡、大康、西方燕、韩、晋三国最为繁荣的贸易之地，后来因为常年战火而衰落下去。;",condition_talk = "",},
	[571] = {id = 571,quest_id = 571,begin_talk = "",end_talk = "[@talk]npc_1§诸葛先生查阅过曲沽河的地理日志，此河的构成颇为奇特，河水中没有任何的鱼类可以生存，却富含钢铁等各类金属矿石，大康建国以前这曲沽河地两岸曾经是淘金者的福地。;",condition_talk = "",},
	[572] = {id = 572,quest_id = 572,begin_talk = "",end_talk = "[@talk]npc_1§宣城最北边的阴山虽然将大康的疆域在此标记，可是越过此山便是广袤的胡而泰草原。;player§可是那胡而泰乃是北胡的地界。;npc_1§你可能不清楚，从阴山北麓往北的五百多里疆域并不是属于北胡，而是居住着十余个部落。这片地域水草肥美，牛羊丰盛，实在是人间乐土。;player§照你们这么说，父皇非但不是流放，反而奖赏。;npc_1§对别人也许是流放，可对公子绝对是奖赏。纳妃之后，你在大康的地位渐渐稳固，前往宣城暂避锋芒，坐观勤王和兴王二虎相争，未必不是一件惬意的事情。;",condition_talk = "",},
	[573] = {id = 573,quest_id = 573,begin_talk = "",end_talk = "[@talk]npc_1§xx，今日我便把楚儿交给你了，你以后一定要好好待她，否则……我……;player§您放心，我一定好好待她，绝不辜负她。;",condition_talk = "",},
	[574] = {id = 574,quest_id = 574,begin_talk = "",end_talk = "[@talk]player§你不开心？;npc_1§楚儿没有不开心。;player§你知不知道，从今晚开始我们便要终生相守在一起。;npc_1§我知道……;",condition_talk = "",},
	[575] = {id = 575,quest_id = 575,begin_talk = "",end_talk = "[@talk]player§今日要去宫中敬茶。;npc_1§我不去！;player§你现在是平王妃，去不去恐怕由不得你！;",condition_talk = "",},
	[576] = {id = 576,quest_id = 576,begin_talk = "",end_talk = "[@talk]player§孩儿来迟，还望父皇、娘娘恕罪！;npc_1§年轻人新婚燕尔，睡得晚些也是难免的，再说你们毕竟没有晚了敬茶的时候。;你们已经是夫妻，以后要抓紧时间给我生几个孙儿。;player§娘娘放心，我们会努力的。;",condition_talk = "",},
	[577] = {id = 577,quest_id = 577,begin_talk = "",end_talk = "[@talk]npc_1§死到临头，还要冒充英雄！;player§小心！保护好王妃，这里我来应付！;",condition_talk = "",},
	[578] = {id = 578,quest_id = 578,begin_talk = "",end_talk = "[@talk]npc_1§你受伤了……;player§不要紧，先回王府！;npc_1§你……你是……;player§我是谁你都不知道了，是不是今日受了惊吓的缘故？;npc_1§你小的时候是不是救过一个小女孩的性命？你这尊护身符的后面最早的时候是不是有一个浮萍的萍字？;player§你怎知道？！;",condition_talk = "",},
	[579] = {id = 579,quest_id = 579,begin_talk = "",end_talk = "[@talk]npc_1§萍哥哥……;player§你是小叮当？！我现在方才明白，你为什么一直都很不开心，原来在你心中只有那个萍哥哥的位置。;npc_1§你当年送给我的面人儿，我一直留在身边。;player§我真的没有想到，当年掉入莲花池的那个小女孩儿竟会是你，我几乎已经忘了这件事……;",condition_talk = "",},
	[580] = {id = 580,quest_id = 580,begin_talk = "",end_talk = "[@talk]npc_1§你去哪里？殿下深夜之中还要苦读吗？;player§楚儿，我不想强迫你，我会等，等到你真正接受我是你相公的事实为止。;npc_1§萍哥哥，我不让你走……楚儿早就已经把你当成了夫君。;player§你心中想的究竟是那个萍哥哥还是我？;npc_1§楚儿已经知道萍哥哥就是你，你就是萍哥哥，自从你冲出去为我挡那一箭的时候……我就知道你是萍哥哥，就是那个会终生保护我的萍哥哥……;",condition_talk = "",},
	[581] = {id = 581,quest_id = 581,begin_talk = "",end_talk = "[@talk]npc_1§刚才我入宫是为了封邑之事。陛下已经决定康都内除了勤王和兴王留下以外，其他的皇子全部前往各自的封邑。;player§本来我还以为只有自己，没想到所有的兄弟都要离开康都。;npc_1§陛下这次的决定倒不失为明智之举。这样一来，诸位皇子之间的矛盾会变得缓和许多，不过……;player§岳父担心勤王和兴王之间的矛盾反而因此事会重点凸出，反而有激化的可能？;",condition_talk = "",},
	[582] = {id = 582,quest_id = 582,begin_talk = "",end_talk = "[@talk]player§岳父还记得当日问过我的一句话吗？岳父曾经问孩儿内心究竟想不想得到大康的皇位？;npc_1§你此刻想对我说实话了吗？;player§孩儿从未将大康的皇位看在眼里……孩儿看重的是这个天下！;npc_1§我……果然没有看错你，你的野心远在其他皇子之上。;",condition_talk = "",},
	[583] = {id = 583,quest_id = 583,begin_talk = "",end_talk = "[@talk]npc_1§你当初答应我照顾楚儿的话难道都忘记了吗？;player§xx不会忘，所以我才选择奋斗抗争而不是退缩和逃避。当初若不是你的反对，楚儿此刻恐怕早已被纳入宫中。正是因为您在朝中拥有不可替代的地位，父皇不得不选择忍让。;npc_1§你……是对的。;",condition_talk = "",},
	[584] = {id = 584,quest_id = 584,begin_talk = "",end_talk = "[@talk]npc_1§你在想什么？;player§我在想阴山夏日的景色一定会很美，到时候我带着你驰骋草原，浏览山色，岂不美哉。;npc_1§我还从未出过远门哩。;player§我们反正不急着赶路，沿途但凡有名胜之处，我们便好好游览一番，这次刚好可以一偿你的心愿。;",condition_talk = "",},
	[585] = {id = 585,quest_id = 585,begin_talk = "",end_talk = "[@talk]npc_1§前方就是曲沽河，大雨仍然没完没了地下着，曲沽河的水位已经上涨了很多。;player§这曲沽河水位如此之高，如果大雨继续下下去，恐怕会有决堤之危，宣城极有可能要面临一场涝灾。;",condition_talk = "",},
	[586] = {id = 586,quest_id = 586,begin_talk = "",end_talk = "[@talk]npc_1§小主人，地图上标志的桥梁都已经被冲毁，我们还是等雨停了再作打算。;player§你带几个人去附近看一看，有没有地方可以供我们暂时歇息，我去弄些野味来，好让大家填饱肚子。;",condition_talk = "",},
	[587] = {id = 587,quest_id = 587,begin_talk = "",end_talk = "[@talk]npc_1§对岸有一艘大船过来了，旗帜上写着一个褚字，看来是褚叔叔派来的船。;player§没想到刚刚来到宣城便要依靠你父王的照顾。;npc_1§褚叔叔每年都会来我家里拜会爹爹，他性情极其爽直，就是有些贪杯。;",condition_talk = "",},
	[588] = {id = 588,quest_id = 588,begin_talk = "",end_talk = "[@talk]npc_1§末将杜飞雄奉褚将军之命特来迎接殿下和王妃一行，还请殿下恕小将接驾来迟。;player§快起来吧，我们也是刚刚到达这里！;",condition_talk = "",},
	[589] = {id = 589,quest_id = 589,begin_talk = "",end_talk = "[@talk]npc_1§这曲沽河上原来有一座桥梁，可是因为年久失修，昨夜被大水冲垮，我们今日上午方才知道桥梁损毁的消息，褚将军临时调拨了这艘船，让我们来迎接平王的大驾。;player§曲沽河的水位不断上涨，宣城的官员竟然没有开始防汛？;",condition_talk = "",},
	[590] = {id = 590,quest_id = 590,begin_talk = "",end_talk = "[@talk]npc_1§据末将所知，地方上动员过几次，可是资金短缺，效率低下，除了在几处缺口之处加强了防汛，其它的地方暂时无法顾及。;player§据我所知，宣城在最近的五年之中已经发生了三次涝灾，难道地方官员还没有引起足够的重视吗？;npc_1§殿下，若想彻底治理宣城的洪涝之灾，必须清除曲沽河河底的淤泥，可是这需要庞大的人力物力，宣城这几年连续欠收，连课税都无法缴上，又哪有钱去兴建水利？;",condition_talk = "",},
	[591] = {id = 591,quest_id = 591,begin_talk = "",end_talk = "[@talk]npc_1§殿下请上车，褚将军早已在军营等待。;player§今日我想先去宣城四处看看，你替我回禀褚将军，改日我再去军营拜访。;npc_1§殿下，天气如此恶劣，再加上饥民众多，您还是先到军营歇息一下，等雨停之后再走。;player§我主意已定，你不必劝我。;",condition_talk = "",},
	[592] = {id = 592,quest_id = 592,begin_talk = "",end_talk = "[@talk]npc_1§这里看来如此贫困，怎地路上却没有见到一个乞丐？;player§乞丐又怎会在这种贫瘠的地方行乞？水往低处流，人往高处走，他们八成都到其他富庶的地区去行乞了。;npc_1§这宣城的地方官吏身为一方父母官，为什么不采取措施？;player§我们去衙门看看便知。;",condition_talk = "",},
	[593] = {id = 593,quest_id = 593,begin_talk = "",end_talk = "[@talk]npc_1§二位是来告状还是来找人？;player§自然是告状！;npc_1§赵大人前往曲沽河察看水情去了，今日不会回来，二位有什么事情便对在下说吧。;player§在下有一事不明，宣城府衙之内为何只有你一人？;npc_1§公子有所不知，宣城此地过于贫瘠，官府已经连续三年未曾发过饷银，衙役也要生活，总不能饿着肚子在这里当差，一个一个便都离开了。;player§这几只山鸡，拿去给周围的百姓们分了吧。;",condition_talk = "",},
	[594] = {id = 594,quest_id = 594,begin_talk = "",end_talk = "[@talk]npc_1§穷固然是件坏事，可也有好的一面，我们整个宣城路不拾遗、夜不闭户。;player§怎么说？;npc_1§路不拾遗是因为无物可拾，夜不闭户是因为家家都没有财物可偷。;player§……",condition_talk = "",},
	[595] = {id = 595,quest_id = 595,begin_talk = "",end_talk = "[@talk]npc_1§这位公子恐怕根本不是来告状的，如果卑职没有猜错，公子便是从康都来的平王殿下。;player§你的眼力倒是不错。;npc_1§小人参见平王千岁、王妃千岁。;player§公孙禄原来你一早便知悉了我的身份，刚才那番话是故意说给我听的，是也不是？;npc_1§平王明鉴！;",condition_talk = "",},
	[596] = {id = 596,quest_id = 596,begin_talk = "",end_talk = "[@talk]npc_1§平王府前有人在施粥，这是不是你的那几位红颜知己的主意？;player§大概是云娜她们在为我建立良好的口碑吧。;npc_1§马上就要见到她们，你为何还不对我说明此事？;player§几位夫人的事我实在是难以开口，我是因为太过紧张你，所以一直隐瞒到了现在。;npc_1§易安什么事情都告诉我了，楚儿并非娇蛮任性之人，你和她们几个相识于患难之时，我又怎会怪你……;",condition_talk = "",},
	[597] = {id = 597,quest_id = 597,begin_talk = "",end_talk = "[@talk]npc_1§主人回来了！;",condition_talk = "",},
	[598] = {id = 598,quest_id = 598,begin_talk = "",end_talk = "[@talk]npc_1§主人，这些是胡人派来的细作，一直暗中收集我们的消息，主人打算如何处置。;player§自然是要教训一二！;",condition_talk = "",},
	[599] = {id = 599,quest_id = 599,begin_talk = "",end_talk = "[@talk]npc_1§怪不得留在康都舍不得回来，原来你的这个林楚儿这么漂亮。;player§你们哪个不是国色天香，当然……你长得虽说差了一些，也勉强能够算上一个绝代佳人。;npc_1§淫贼！;",condition_talk = "",},
	[600] = {id = 600,quest_id = 600,begin_talk = "",end_talk = "[@talk]npc_1§这宣城的情况比我们预想的还要糟糕，我们来到之后，看到这里灾情严重，便动用了部分资金去临近的州县购粮，可是僧多粥少，我们自己的力量毕竟太过薄弱。;player§这件事我已经考虑过，马上我就会上书父皇，要求他免去宣城今年的赋税。;npc_1§前些日子我和这里的知府赵大人谈过此事，这三年以来他上书无数，可是歆德皇始终不为所动，坚持让宣城缴纳课税。;player§当初父皇赏赐给我的十一万金，直到今日都没有兑现，我便用那笔赏钱来抵消宣城的课税。;",condition_talk = "",},
	[601] = {id = 601,quest_id = 601,begin_talk = "",end_talk = "[@talk]npc_1§我虽然不懂得什么大道理，可是坐吃山空也不是办法，便是我们动用所有的财产，也难以解决整个宣城居民的困难。;player§所以要想摆脱困境必须依靠宣城百姓自己。;",condition_talk = "",},
	[602] = {id = 602,quest_id = 602,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，刚刚得到曲沽河方面的消息，临子坡河段出现险情，我正在说服百姓前去相助。;player§情况怎么样？;npc_1§水位仍然在不断上涨，恐怕大堤要保不住了。;player§你去宣城军营，向褚大壮将军求援。;",condition_talk = "",},
	[603] = {id = 603,quest_id = 603,begin_talk = "",end_talk = "[@talk]npc_1§平王大概不知道，陛下有令，军队与民政必须分开，各负其责，调用军队想要得到朝廷的批准。;player§宣城是我的封邑，这里的土地由我治理，无论是兵是民，只要生活在这片土地上，便有义务保卫家园！;",condition_talk = "",},
	[604] = {id = 604,quest_id = 604,begin_talk = "",end_talk = "[@talk]npc_1§可是朝廷派监官监查军队调用，这该如何是好？;player§如此不明是非，不要也罢！;",condition_talk = "",},
	[605] = {id = 605,quest_id = 605,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，我调配了五千兵马和数千个麻袋，希望可以派上用场。;player§哈哈哈，好！兄弟们！今天就是用身体我们也要堵住洪水！;",condition_talk = "",},
	[606] = {id = 606,quest_id = 606,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，宣城知府赵东齐拜见平王千岁千千岁！;player§赵大人快快请起。;npc_1§今日若不是殿下及时赶到，大堤肯定不保，宣城必然被淹。;player§宣城乃是本王的封邑，本王自当全力造福这一方百姓，第一件事就是要保住他们的家园。;",condition_talk = "",},
	[607] = {id = 607,quest_id = 607,begin_talk = "",end_talk = "[@talk]npc_1§这曲沽河每年都会泛滥，这两年虽然不断加高堤坝，可是上游来的河水掺杂着大量泥沙，让河床不断升高，如今河床已经高出地面，成为名副其实的悬河。;player§此次雨季过后，我想方设法清淤。;",condition_talk = "",},
	[608] = {id = 608,quest_id = 608,begin_talk = "",end_talk = "[@talk]npc_1§下官身为一方父母官，却将宣城治理成如此模样，还望平王治罪。;player§赵大人，我来到这里便是想看到一个祥和的宣城、富庶的宣城，从今日起，我会尽量提供给你一切的便利，赵大人尽管放手去做吧！;",condition_talk = "",},
	[609] = {id = 609,quest_id = 609,begin_talk = "",end_talk = "[@talk]npc_1§小主子快看哪！天放晴了！;",condition_talk = "",},
	[610] = {id = 610,quest_id = 610,begin_talk = "",end_talk = "[@talk]npc_1§下官认为如今当务之急还是治理曲沽河，只有改善宣城的环境，才能让百姓慢慢地回到耕作之中。;npc_1§可是这需要一笔巨大的资金，以宣城目前的财政状况，恐怕只是天方夜谭。;player§不知道你们有没有听说过，当年曲沽河两岸乃是淘金的热土？;",condition_talk = "",},
	[611] = {id = 611,quest_id = 611,begin_talk = "",end_talk = "[@talk]npc_1§的确有这样的事情，不过已经是大康建国之前的事情，最后一个淘金场也在二十年前就已经关闭，宣城现在已经没有金砂了。;player§如果我们重开淘金场，而且产出大量的金砂，会怎么样？;npc_1§若是能够淘出大量的金砂，恐怕整个大康的淘金商人都会蜂拥到这里来。;",condition_talk = "",},
	[612] = {id = 612,quest_id = 612,begin_talk = "",end_talk = "[@talk]npc_1§宣城要想迅速恢复元气，必须要有大量的资金注入，平王的意思可能是借着淘金之事吸引大量的客商来到宣城，带动宣城一方的经济。;player§我找你们来，便是想联合上演一出好戏。;npc_1§我们只需抓出一帮贪官污吏，说是他们当年刻意隐瞒了金沙的数量即可。;player§哈哈哈，顺便还能借机除掉这些害群之马。;",condition_talk = "",},
	[613] = {id = 613,quest_id = 613,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下放心，我们一定全力配合此事。;player§马上起草一份告示，在我开工之后，严令百姓不得接近曲沽河周围的金矿，更不许百姓私人淘金，凡有违抗命令者，定斩不饶。;",condition_talk = "",},
	[614] = {id = 614,quest_id = 614,begin_talk = "",end_talk = "[@talk]npc_1§可是当淘金商人来到宣称之后发现宣城并没有资源丰富的金砂会怎么样？;player§这件事，我自有安排。;",condition_talk = "",},
	[615] = {id = 615,quest_id = 615,begin_talk = "",end_talk = "[@talk]npc_1§相公，真的会有这么多商人会上这个当吗？;player§我已经让唐昧和突藉去晋国购入大量的金砂，金矿一旦开始生产，金砂的产量就会让人瞠目结舌。;npc_1§看来过不了多久，宣城在天下人眼中就会变成一块宝地了。;",condition_talk = "",},
	[616] = {id = 616,quest_id = 616,begin_talk = "",end_talk = "[@talk]npc_1§公子，我回来了，二百三十五个金矿已经全部租了出去。;player§收到了多少银子？;npc_1§主人猜一猜。;player§五十万金？;npc_1§共计卖出了二百六十万金！;",condition_talk = "",},
	[617] = {id = 617,quest_id = 617,begin_talk = "",end_talk = "[@talk]npc_1§之所以能够排出如此高的天价，公子还需感谢你的一位老朋友。;player§哦？;",condition_talk = "",},
	[618] = {id = 618,quest_id = 618,begin_talk = "",end_talk = "[@talk]npc_1§普天之下谁人不知道宣城发现大量金矿的事情？但凡是商人皆以利益为先，钱某自付和平王殿下有几番交情，自然要来讨个便宜。;player§四海兄难道真的改行做起了黄金生意？;npc_1§钱某两手空空来到宣城，自然想送给平王一份大礼，哄抬价格，制造行市本来就是我的强项，钱某只需动动嘴皮，再联系一两个同僚，平王的这次拍卖必然会红红火火，获利非常！;player§四海兄果然厉害。;",condition_talk = "",},
	[619] = {id = 619,quest_id = 619,begin_talk = "",end_talk = "[@talk]npc_1§平王殿下，出大事了……安王他……他在北胡病逝了……;player§什么？;npc_1§殿下还请节哀。;player§皇兄今年才不过二十二岁，怎会突然早逝，真是天妒英才。;",condition_talk = "",},
	[620] = {id = 620,quest_id = 620,begin_talk = "",end_talk = "[@talk]npc_1§前来送信的北胡信使，送完信后竟然打算偷偷离开，实在是奇怪得很。;player§看来此事大有蹊跷。;",condition_talk = "",},
	[621] = {id = 621,quest_id = 621,begin_talk = "",end_talk = "[@talk]npc_1§还有一件事……皇上让平王殿下前往吊丧……;player§这种时候，父皇倒是把我记得很清楚。;",condition_talk = "",},
	[622] = {id = 622,quest_id = 622,begin_talk = "",end_talk = "[@talk]npc_1§不能去，你刚刚才从秦国回来，怎么又让你去北胡吊丧，你父皇有三十多个儿子为什么偏偏要选中你呢？;player§哈哈，你是不是怕我去北胡之后，被北胡可汗强自扣留下来做女婿？;npc_1§胡人生性野蛮，做事不讲情理，他们这么做也不是没有可能。;",condition_talk = "",},
	[623] = {id = 623,quest_id = 623,begin_talk = "",end_talk = "[@talk]npc_1§北胡可汗拓跋寿缮为人喜怒无常，不易相处，更何况安王这次的死因未明，相公若是前去，肯定是凶险异常。;player§父皇已经降旨，让我前往北胡吊丧，我若是不去，便是有违皇命，再者说，死去的是我的皇兄，我若是不去，也于理不合。;npc_1§我和你一起去北胡！;",condition_talk = "",},
	[624] = {id = 624,quest_id = 624,begin_talk = "",end_talk = "[@talk]npc_1§我随你去有充分的理由，第一我是平王妃，跟随你前去吊唁合乎两国礼仪；第二，有我在你身边自然不用担心北胡的什么公主看中了你，将你留下做她的驸马。;player§王妃既然决定了，我这个平王也只好乖乖听命了。;",condition_talk = "",},
	[625] = {id = 625,quest_id = 625,begin_talk = "",end_talk = "[@talk]npc_1§这次你前往北胡的路线一定要考虑周详，千万要避免可能出现的危险。;player§我决定从阴山抄捷径进入北胡。;npc_1§你明明知道那阴山之中有若干野蛮部落，为何友还要选择这条道路？;player§我想在皇兄入土之前赶到北胡，只有走这条路。;",condition_talk = "",},
	[626] = {id = 626,quest_id = 626,begin_talk = "",end_talk = "[@talk]npc_1§王正曾经多次前往北胡刺探过军情，对经阴山前往北胡的道路相当熟悉，这次就让他给殿下当向导。;player§我不在的这段时间，这里交给你了。;npc_1§属下定当竭尽全力，保护大家的安全。;",condition_talk = "",},
	[627] = {id = 627,quest_id = 627,begin_talk = "",end_talk = "[@talk]npc_1§这里原有不少牧民，可是后来因为不堪忍受阴山胡虏部落的滋扰，全都离开了这里。;player§褚将军没有在这附近驻守军队？;npc_1§往西事物里便有一个骑兵营，他们负责这一带的警戒。不过，阴山胡虏神出鬼没，每次突袭都是迅速异常，往往等到我军赶到的时候，这些胡虏早已撤离。;player§我军的装备实在太差。;",condition_talk = "",},
	[628] = {id = 628,quest_id = 628,begin_talk = "",end_talk = "[@talk]npc_1§这里入夜后常有野兽出没，大家不可分散开来。;player§看来我们的运气不是太好，你看那是什么？;npc_1§棕熊！大家小心！;player§听说这熊胆是个珍贵药材，我就把它带回去送给慧乔。;",condition_talk = "",},
	[629] = {id = 629,quest_id = 629,begin_talk = "",end_talk = "[@talk]npc_1§我去树林前方的山冈看看，有没有外敌的踪迹。;player§我和楚儿跟你一起去，顺便浏览一下阴山的夜景。;npc_1§你们听，是不是有人在喊救命？;",condition_talk = "",},
	[630] = {id = 630,quest_id = 630,begin_talk = "",end_talk = "[@talk]npc_1§救命啊！救命啊！;",condition_talk = "",},
	[631] = {id = 631,quest_id = 631,begin_talk = "",end_talk = "[@talk]player§怎会有一个孤身女子这个时候孩子山野之中？;npc_1§公子，如果我们不施以援手，她被野狼围困，岂不是异常危险。;player§先去救她，若是发现有什么怪异之处，便一刀结果了她。;",condition_talk = "",},
	[632] = {id = 632,quest_id = 632,begin_talk = "",end_talk = "[@talk]npc_1§多谢壮士救命之恩。;player§救你的是唐昧，我们并没有帮上什么。;你为何会孤身一人在此？;npc_1§我家住在前方的穿云谷，今日牧羊之时，发现一只小羊羔不见了，便一路赶了过来，却不想遇上了狼群。;",condition_talk = "",},
	[633] = {id = 633,quest_id = 633,begin_talk = "",end_talk = "[@talk]npc_1§你有没有留意到，唐昧今日有些奇怪哩。;player§我担心的就是这件事，唐昧从来没有对别的女子脸红过，今日他对这个普蔓好像有特别的好感。;npc_1§你这就不对了，难道只许州官放火不许百姓点灯，你终日坐拥花丛，却忍心看着唐大哥孤苦伶仃？;player§我并不是这个意思，只是这个普蔓着实有些奇怪。;",condition_talk = "",},
	[634] = {id = 634,quest_id = 634,begin_talk = "",end_talk = "[@talk]npc_1§也许她真的是一个牧羊女呢，再说你干此也让唐昧察看过周围，并没有其他人在。;player§先不去管她，我们是不是享受一下阴山的野趣？;npc_1§你这个荒淫无道的家伙，何时何地总想着这些事情。;",condition_talk = "",},
	[635] = {id = 635,quest_id = 635,begin_talk = "",end_talk = "[@talk]npc_1§我们今日是不是带她一起上路，顺便将她送回家去，她一个女孩子在这荒山野岭之中，恐怕会不安全。;player§你看着办吧。;",condition_talk = "",},
	[636] = {id = 636,quest_id = 636,begin_talk = "",end_talk = "[@talk]npc_1§还有一里多路便到穿云谷了。啊……有陷阱！;",condition_talk = "",},
	[637] = {id = 637,quest_id = 637,begin_talk = "",end_talk = "[@talk]npc_1§大家小心，先冲出这段线路再说。;player§用弩箭瞄准普蔓，她有任何的异动便一箭射死她。;npc_1§属下明白。;",condition_talk = "",},
	[638] = {id = 638,quest_id = 638,begin_talk = "",end_talk = "[@talk]npc_1§将财物和女人留下，我放你们离开。;player§那得要看你有没有这个本事。;",condition_talk = "",},
	[639] = {id = 639,quest_id = 639,begin_talk = "",end_talk = "[@talk]npc_1§我要去救普蔓！;player§唐昧小心！;",condition_talk = "",},
	[640] = {id = 640,quest_id = 640,begin_talk = "",end_talk = "[@talk]npc_1§唐昧！你不要吓我……是我害了你……对不起……对不起……;player§滚开！;npc_1§救他，求你救他！;player§要不是你，他也不会受伤！滚！;",condition_talk = "",},
	[641] = {id = 641,quest_id = 641,begin_talk = "",end_talk = "[@talk]npc_1§不许走！;player§普蔓姑娘还想做什么，难道非要拼上一个你死我活，你才甘心？;npc_1§唐昧伤势太重，你们之中又没人懂得医治，如果你坚持上路，恐怕他会凶多吉少。;",condition_talk = "",},
	[642] = {id = 642,quest_id = 642,begin_talk = "",end_talk = "[@talk]npc_1§我妹妹并没有骗你们，不如你们跟随我到穿云谷暂且歇息两日，等到他伤势稳定再作打算。;player§我凭什么相信你？;",condition_talk = "",},
	[643] = {id = 643,quest_id = 643,begin_talk = "",end_talk = "[@talk]npc_1§我知道，你们是无论如何都不会相信我的，我可以留在你们手中作为人质！;player§哦？唐昩有什么值得你这么为他？;npc_1§从他不顾性命地返回来救我，那一刻我便喜欢上了她。;今生今世我心中便只有他的影子。;",condition_talk = "",},
	[644] = {id = 644,quest_id = 644,begin_talk = "",end_talk = "[@talk]npc_1§我要杀了他！普蔓是我的女人！;",condition_talk = "",},
	[645] = {id = 645,quest_id = 645,begin_talk = "",end_talk = "[@talk]npc_1§我看这普蔓是对唐昧动了真情，刚才她伤到了唐昧已经深感愧疚，况且唐昧伤得不轻，如果我们坚持上路对他的确没有好处。;player§好吧，我们就暂时留下来。;",condition_talk = "",},
	[646] = {id = 646,quest_id = 646,begin_talk = "",end_talk = "[@talk]player§既然你们也属于胡人，为何没有归入北胡？;npc_1§这阴山之中有叉塔族、吉克拉族、诺兰族、穆歧族四部，可后来都被勃而金族击败，草原也被他们统一。我们侥幸逃到了阴山之中，可他们从未放弃过对我们的欺压和征讨，我们的族人也不断地减少……;player§你只知道感伤自己族人的命运，可是抢掠我们汉人的时候，有没有任何的同情心？;",condition_talk = "",},
	[647] = {id = 647,quest_id = 647,begin_talk = "",end_talk = "[@talk]npc_1§我可以和你单独谈谈吗？;player§请说。;npc_1§你和那个唐昧一样都是好汉子！;player§你将我喊到这里来，恐怕不仅仅是向我说一句恭维话这么简单。;",condition_talk = "",},
	[648] = {id = 648,quest_id = 648,begin_talk = "",end_talk = "[@talk]npc_1§我只有普蔓这一个妹子，她从来没有像对待唐昧这样对待过别的男人，我想她已经爱上他了。;player§只可惜你妹妹的心上人却差一点死在你这个当哥哥的矛下，若是他死了，恐怕普蔓今生今世都不会原谅你。;npc_1§我想和商量一下，能不能让唐昧暂时留在这里，等到他养好伤之后，我再送他离去。;player§这件事我要征求一下唐昧的意见。;",condition_talk = "",},
	[649] = {id = 649,quest_id = 649,begin_talk = "",end_talk = "[@talk]npc_1§都是唐昧误信妖女，连累公子身涉险境……;player§唐昧，其实普蔓并不是有心害你，如果不是她，你恐怕早就死在了雅克的矛下。;npc_1§可是……;player§不要忘了，你是一个男子汉，难道还要和一个女子计较不成？我只问你一句话，你究竟对普蔓有没有感情？;npc_1§我不清楚……只知道若是她有什么危险，我会拼着自己的性命去救她。;",condition_talk = "",},
	[650] = {id = 650,quest_id = 650,begin_talk = "",end_talk = "[@talk]npc_1§谢谢你，我害了你们，你却还愿意化解我和唐昧的矛盾。;player§我只希望唐昧能幸福，只是那个蒙多里能就此罢手吗？;",condition_talk = "",},
	[651] = {id = 651,quest_id = 651,begin_talk = "",end_talk = "[@talk]npc_1§这报告殿下！王正死了！蒙多里的手下杀了他！;",condition_talk = "",},
	[652] = {id = 652,quest_id = 652,begin_talk = "",end_talk = "[@talk]npc_1§我听唐昧说，昨晚死去的是你们的向导？;player§正是。;npc_1§这件事是因我而起，我难辞其咎，不如这样，我亲自将你们送往北胡，以表达我心中的歉意。;player§兄台又何须如此客气，王正虽然死了，可是我们还有路线图可供参详。;",condition_talk = "",},
	[653] = {id = 653,quest_id = 653,begin_talk = "",end_talk = "[@talk]npc_1§阴山道路复杂，如果没有一个熟知地形的人带路，你们恐怕很难顺利抵达北胡。况且这阴山之中并不仅仅只有我们叉塔族生活，我带你们过去，可以避免很多不必要的麻烦。;player§既然如此，便麻烦雅克壮士了。;npc_1§你们以后便叫我雅克便行，不要显得如此生分。;",condition_talk = "",},
	[654] = {id = 654,quest_id = 654,begin_talk = "",end_talk = "[@talk]npc_1§我还从未问过，你们前往乌库苏城究竟是为了什么事情？;player§实不相瞒，我乃是大康平王，此次前来是为了奔丧。;npc_1§你便是统治宣城的平王？听说你来到宣地之后，将你里搞得有声有色，我等正准备前往突袭。;player§你们再敢前去滋扰百姓恐怕会损失惨重。;npc_1§原来我未必会相信，可是你若是宣城的平王，这件事我要重新考虑了。;",condition_talk = "",},
	[655] = {id = 655,quest_id = 655,begin_talk = "",end_talk = "[@talk]npc_1§前面就是北胡境地，雅克不送了。;player§多谢这一路的照顾。;npc_1§希望平王早日回来，参加妹妹和唐兄的婚礼！;player§哈哈哈，一定！;",condition_talk = "",},
