	[1003] = {id = 1003,quest_id = 1003,begin_talk = "[@talk]npc_503§看公子颇有慧根，老夫便赠你一言，“手挥大风平天下，脚踏日月鼎乾坤“公子乃帝皇之相……只是……造化弄人啊。;<exit>",end_talk = "",condition_talk = "",},
	[1004] = {id = 1004,quest_id = 1004,begin_talk = "[@talk] npc_515§公子救救我， 有人在追我，我好怕~; player§光天化日之下，竟敢调戏民女，我今天就来替天行道！;<exit>",end_talk = "[@talk]npc_515§多谢公子救命之恩，敢问公子尊姓大名？;<exit>",condition_talk = "",},
	[1005] = {id = 1005,quest_id = 1005,begin_talk = "[@talk] player§侍从追上来了，我们快去元宵灯会，混在人群中，或许能避开他们。;<exit>",end_talk = "",condition_talk = "",},
